import React, { useState } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';

const ingredients = ['Flour', 'Sugar', 'Butter', 'Eggs', 'Milk'];

export default function RecipeEditor() {
  const [title, setTitle] = useState('');
  const [yieldAmount, setYieldAmount] = useState('');
  const [ingredientLines, setIngredientLines] = useState([
    { qty: '', unit: 'g', ingredient: '', modifier: '' },
  ]);

  const [units, setUnits] = useState([
    'g', 'oz', 'ea', 'qts', 'lb', 'ml', 'cup', 'tbsp', 'tsp'
  ]);
  const [showUnitManager, setShowUnitManager] = useState(false);

  const handleChange = (index, field, value) => {
    const updatedLines = [...ingredientLines];
    updatedLines[index][field] = value;
    setIngredientLines(updatedLines);
  };

  const handleUnitChange = (index, value) => {
    if (value === '__add__') {
      const customUnit = prompt('Enter custom unit:');
      if (customUnit && !units.includes(customUnit)) {
        setUnits([...units, customUnit]);
        handleChange(index, 'unit', customUnit);
      }
    } else {
      handleChange(index, 'unit', value);
    }
  };

  const addIngredientLine = () => {
    setIngredientLines([...ingredientLines, { qty: '', unit: 'g', ingredient: '', modifier: '' }]);
  };

  const removeIngredientLine = (index) => {
    const updatedLines = ingredientLines.filter((_, i) => i !== index);
    setIngredientLines(updatedLines);
  };

  const convertUnit = (qty, unit) => {
    if (!qty || isNaN(qty)) return null;
    const q = parseFloat(qty);
    if (unit === 'g') return `${(q * 0.0353).toFixed(2)} oz`;
    if (unit === 'oz') return `${(q * 28.35).toFixed(0)} g`;
    return null;
  };

  const updateUnit = (index) => {
    const newUnit = prompt('Edit unit:', units[index]);
    if (newUnit && newUnit !== units[index]) {
      const updated = [...units];
      updated[index] = newUnit;
      setUnits(updated);
    }
  };

  const deleteUnit = (index) => {
    const updated = [...units];
    updated.splice(index, 1);
    setUnits(updated);
  };

  const handleDragEnd = (result) => {
    if (!result.destination) return;
    const items = Array.from(units);
    const [reordered] = items.splice(result.source.index, 1);
    items.splice(result.destination.index, 0, reordered);
    setUnits(items);
  };

  return (
    <div className="max-w-4xl mx-auto p-4 font-satoshi text-gray-800">
      {/* Title and Yield */}
      <div className="mb-6">
        <input
          type="text"
          placeholder="Recipe Title"
          className="text-2xl font-semibold w-full border-b border-gray-400 focus:outline-none pb-1"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <input
          type="text"
          placeholder="Yield (e.g., 12 slices)"
          className="mt-2 text-base border-b border-gray-300 focus:outline-none w-full pb-1"
          value={yieldAmount}
          onChange={(e) => setYieldAmount(e.target.value)}
        />
      </div>

      {/* Ingredient Lines */}
      {ingredientLines.map((line, index) => (
        <div
          key={index}
          className="flex flex-wrap items-end gap-x-2 gap-y-1 mb-2 pb-1 border-b border-gray-200"
        >
          {/* Qty */}
          <input
            type="number"
            placeholder="Qty"
            className="w-20 text-sm border-none border-b border-gray-400 focus:outline-none underline font-medium text-gray-900"
            value={line.qty}
            onChange={(e) => handleChange(index, 'qty', e.target.value)}
          />

          {/* Unit */}
          <select
            value={line.unit}
            onChange={(e) => handleUnitChange(index, e.target.value)}
            className="w-20 text-sm border-none border-b border-gray-400 bg-transparent focus:outline-none underline font-medium text-gray-900"
          >
            {units.map((u) => (
              <option key={u} value={u}>
                {u}
              </option>
            ))}
            <option value="__add__">➕ Custom</option>
          </select>

          {/* Conversion */}
          <span className="text-xs text-gray-500 min-w-[70px]">
            {line.qty && convertUnit(line.qty, line.unit)}
          </span>

          {/* Ingredient */}
          <select
            value={line.ingredient}
            onChange={(e) => handleChange(index, 'ingredient', e.target.value)}
            className="flex-1 min-w-[150px] text-sm border-none border-b border-gray-300 bg-transparent focus:outline-none font-semibold text-gray-900"
          >
            <option value="" disabled>
              Ingredient
            </option>
            {ingredients.map((ing) => (
              <option key={ing} value={ing}>
                {ing}
              </option>
            ))}
          </select>

          {/* Modifier */}
          <input
            type="text"
            placeholder="Modifier (e.g., Instant)"
            className="text-xs italic text-gray-500 border-none border-b border-gray-200 bg-transparent focus:outline-none w-32"
            value={line.modifier}
            onChange={(e) => handleChange(index, 'modifier', e.target.value)}
          />

          {/* Remove */}
          <button
            onClick={() => removeIngredientLine(index)}
            className="text-xs text-red-500 hover:text-red-700 pl-2"
          >
            Remove
          </button>
        </div>
      ))}

      {/* Add Ingredient */}
      <div className="mt-4 flex items-center gap-4">
        <button
          onClick={addIngredientLine}
          className="px-4 py-1 text-sm bg-blue-600 text-white rounded hover:bg-blue-700 transition"
        >
          + Add Ingredient
        </button>
        <button
          onClick={() => setShowUnitManager((prev) => !prev)}
          className="px-3 py-1 text-sm text-gray-700 hover:underline"
        >
          ⚙️ Manage Units
        </button>
      </div>

      {/* Unit Manager */}
      {showUnitManager && (
        <div className="mt-6 p-4 border rounded bg-gray-50">
          <h3 className="text-lg font-semibold mb-2">Manage Units</h3>
          <DragDropContext onDragEnd={handleDragEnd}>
            <Droppable droppableId="units">
              {(provided) => (
                <ul
                  className="space-y-2"
                  {...provided.droppableProps}
                  ref={provided.innerRef}
                >
                  {units.map((unit, index) => (
                    <Draggable key={unit} draggableId={unit} index={index}>
                      {(provided) => (
                        <li
                          className="flex items-center gap-2 bg-white p-2 shadow-sm rounded"
                          ref={provided.innerRef}
                          {...provided.draggableProps}
                          {...provided.dragHandleProps}
                        >
                          <span className="flex-1 text-sm">{unit}</span>
                          <button
                            className="text-blue-500 text-xs"
                            onClick={() => updateUnit(index)}
                          >
                            Edit
                          </button>
                          <button
                            className="text-red-500 text-xs"
                            onClick={() => deleteUnit(index)}
                          >
                            Delete
                          </button>
                        </li>
                      )}
                    </Draggable>
                  ))}
                  {provided.placeholder}
                </ul>
              )}
            </Droppable>
          </DragDropContext>
        </div>
      )}
    </div>
  );
}
