import React, { useState } from 'react';

const NewRecipeForm = ({ onClose }) => {
  const [ingredientsText, setIngredientsText] = useState('');
  const [methodText, setMethodText] = useState('');

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-50">
      <div className="bg-white p-6 rounded-lg max-w-4xl w-full shadow-lg relative">
        <button
          onClick={onClose}
          className="absolute top-2 right-3 text-2xl font-bold text-gray-500 hover:text-black"
        >
          Ã—
        </button>

        <h2 className="text-2xl font-bold mb-4">Add Ingredients & Prep Method</h2>
        <p className="text-gray-600 mb-4">
          Type or copy/paste ingredients and/or prep steps from any word doc, spreadsheet, PDF, or website.
        </p>

        <div className="grid grid-cols-2 gap-4">
          <div>
            <label className="font-semibold">Ingredients</label>
            <textarea
              className="w-full h-56 p-2 border mt-1"
              placeholder="Paste ingredients here..."
              value={ingredientsText}
              onChange={(e) => setIngredientsText(e.target.value)}
            />
            <p className="text-sm text-gray-500 mt-1">
              Add <span className="font-mono">(note)</span> to notes. Use headers with a colon (e.g., <span className="font-mono">To Garnish:</span>)
            </p>
          </div>

          <div>
            <label className="font-semibold">Prep Method</label>
            <textarea
              className="w-full h-56 p-2 border mt-1"
              placeholder="Paste prep method here..."
              value={methodText}
              onChange={(e) => setMethodText(e.target.value)}
            />
            <p className="text-sm text-gray-500 mt-1">
              Add <span className="font-mono">(note)</span> to notes. Use headers with a colon (e.g., <span className="font-mono">To Garnish:</span>)
            </p>
          </div>
        </div>

        <div className="mt-6 flex justify-between">
          <button
            onClick={onClose}
            className="text-blue-600 hover:underline"
          >
            Skip
          </button>
          <button
            onClick={() => {
              // Parser logic to come later
              alert('Parser not implemented yet');
              onClose();
            }}
            className="bg-indigo-500 text-white px-6 py-2 rounded hover:bg-indigo-600"
          >
            Add Recipe
          </button>
        </div>
      </div>
    </div>
  );
};

export default NewRecipeForm;
