import React, { useState, useEffect } from 'react';

export default function IngredientManager() {
  const [ingredients, setIngredients] = useState(() => {
    const saved = localStorage.getItem('ingredientCatalog');
    return saved ? JSON.parse(saved) : [];
  });

  const [form, setForm] = useState({
    name: '',
    category: '',
    subcategory: '',
    apCost: '',
    packSize: '',
    packUnit: 'g',
    id: null
  });

  useEffect(() => {
    localStorage.setItem('ingredientCatalog', JSON.stringify(ingredients));
  }, [ingredients]);

  const handleChange = (field, value) => {
    setForm(prev => ({ ...prev, [field]: value }));
  };

  const handleSubmit = () => {
    const { name, category, subcategory, apCost, packSize, packUnit, id } = form;
    if (!name || !category || !subcategory || !apCost || !packSize) return;

    const newItem = {
      id: id || Date.now().toString(),
      name,
      category,
      subcategory,
      apCost: parseFloat(apCost),
      packSize: parseFloat(packSize),
      packUnit
    };

    if (id) {
      setIngredients(prev => prev.map(i => (i.id === id ? newItem : i)));
    } else {
      setIngredients(prev => [...prev, newItem]);
    }

    setForm({
      name: '',
      category: '',
      subcategory: '',
      apCost: '',
      packSize: '',
      packUnit: 'g',
      id: null
    });
  };

  const handleEdit = (ingredient) => {
    setForm({
      ...ingredient,
      apCost: ingredient.apCost.toString(),
      packSize: ingredient.packSize.toString()
    });
  };

  const handleDelete = (id) => {
    if (window.confirm('Delete this ingredient?')) {
      setIngredients(prev => prev.filter(i => i.id !== id));
    }
  };

  const groupedIngredients = ingredients.reduce((acc, ing) => {
    const { category, subcategory } = ing;
    if (!acc[category]) acc[category] = {};
    if (!acc[category][subcategory]) acc[category][subcategory] = [];
    acc[category][subcategory].push(ing);
    return acc;
  }, {});

  return (
    <div className="p-6 max-w-4xl mx-auto bg-white shadow-lg rounded-lg">
      <h2 className="text-2xl font-semibold mb-6 text-gray-800">
        {form.id ? 'Edit Ingredient' : 'Add New Ingredient'}
      </h2>

      <div className="grid grid-cols-3 gap-4 mb-6">
        <input
          type="text"
          placeholder="Name"
          value={form.name}
          onChange={(e) => handleChange('name', e.target.value)}
          className="border rounded p-2"
        />
        <input
          type="text"
          placeholder="Category"
          value={form.category}
          onChange={(e) => handleChange('category', e.target.value)}
          className="border rounded p-2"
        />
        <input
          type="text"
          placeholder="Subcategory"
          value={form.subcategory}
          onChange={(e) => handleChange('subcategory', e.target.value)}
          className="border rounded p-2"
        />
        <input
          type="number"
          placeholder="AP Cost ($)"
          value={form.apCost}
          onChange={(e) => handleChange('apCost', e.target.value)}
          className="border rounded p-2"
        />
        <input
          type="number"
          placeholder="Pack Size"
          value={form.packSize}
          onChange={(e) => handleChange('packSize', e.target.value)}
          className="border rounded p-2"
        />
        <select
          value={form.packUnit}
          onChange={(e) => handleChange('packUnit', e.target.value)}
          className="border rounded p-2"
        >
          <option value="g">grams</option>
          <option value="kg">kilograms</option>
          <option value="oz">ounces</option>
          <option value="lb">pounds</option>
          <option value="ml">milliliters</option>
          <option value="l">liters</option>
          <option value="each">each</option>
        </select>
      </div>

      <div className="flex gap-4 mb-6">
        <button
          onClick={handleSubmit}
          className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
        >
          {form.id ? 'Update Ingredient' : '+ Add Ingredient'}
        </button>
        {form.id && (
          <button
            onClick={() =>
              setForm({
                name: '',
                category: '',
                subcategory: '',
                apCost: '',
                packSize: '',
                packUnit: 'g',
                id: null
              })
            }
            className="bg-gray-500 text-white px-4 py-2 rounded hover:bg-gray-600"
          >
            Cancel
          </button>
        )}
      </div>

      {Object.entries(groupedIngredients).map(([category, subs]) => (
        <div key={category} className="mb-4">
          <h3 className="text-xl font-bold text-blue-700">{category}</h3>
          {Object.entries(subs).map(([subcategory, items]) => (
            <div key={subcategory} className="ml-4">
              <h4 className="text-md font-semibold text-blue-500">{subcategory}</h4>
              <ul className="ml-4 list-disc">
                {items.map(ing => (
                  <li key={ing.id} className="mb-1 flex justify-between items-center">
                    <span>
                      {ing.name} â€“ ${ing.apCost.toFixed(2)} per {ing.packSize} {ing.packUnit}
                    </span>
                    <div className="space-x-2">
                      <button
                        onClick={() => handleEdit(ing)}
                        className="text-blue-600 text-sm hover:underline"
                      >
                        Edit
                      </button>
                      <button
                        onClick={() => handleDelete(ing.id)}
                        className="text-red-600 text-sm hover:underline"
                      >
                        Delete
                      </button>
                    </div>
                  </li>
                ))}
              </ul>
            </div>
          ))}
        </div>
      ))}
    </div>
  );
}
