import React, { useState } from 'react';
import IngredientRow from './IngredientRow';
import ingredientsDB from './Ingredients';

export default function IngredientManager() {
  const [ingredientLines, setIngredientLines] = useState([
    { qty: '', unit: 'oz', ingredientId: '', modifier: '' },
  ]);

  const handleChange = (index, field, value) => {
    const updated = [...ingredientLines];
    updated[index][field] = value;
    setIngredientLines(updated);
  };

  const addIngredientLine = () => {
    setIngredientLines([
      ...ingredientLines,
      { qty: '', unit: 'oz', ingredientId: '', modifier: '' },
    ]);
  };

  const removeIngredientLine = (index) => {
    const updated = ingredientLines.filter((_, i) => i !== index);
    setIngredientLines(updated);
  };

  return (
    <div className="space-y-4">
      {ingredientLines.map((line, index) => (
        <IngredientRow
          key={index}
          index={index}
          line={line}
          onChange={handleChange}
          onRemove={removeIngredientLine}
        />
      ))}

      <button
        onClick={addIngredientLine}
        className="px-4 py-2 text-sm bg-blue-600 text-white rounded hover:bg-blue-700 transition"
      >
        + Add Ingredient
      </button>
    </div>
  );
}
