import React, { useState, useEffect } from 'react';

const ingredientCosts = {
  Flour: { unitCost: 0.02, unit: "g" },
  Butter: { unitCost: 0.10, unit: "g" },
  Eggs: { unitCost: 0.25, unit: "each" },
  Sugar: { unitCost: 0.015, unit: "g" }
};

function calculateLineCost(qty, ingredientName) {
  const costData = ingredientCosts[ingredientName];
  if (!costData) return 0;
  return parseFloat((qty * costData.unitCost).toFixed(2));
}

export default function RecipeEditor() {
  const [lines, setLines] = useState(() => {
    const saved = localStorage.getItem('recipeLines');
    return saved ? JSON.parse(saved) : [{
      id: Date.now(),
      qty: '',
      unit: '',
      ingredient: '',
      lineCost: 0
    }];
  });

  // Save to localStorage when lines change
  useEffect(() => {
    localStorage.setItem('recipeLines', JSON.stringify(lines));
  }, [lines]);

  const handleChange = (id, field, value) => {
    setLines(prev =>
      prev.map(line => {
        if (line.id !== id) return line;
        const updated = { ...line, [field]: value };
        if (field === 'qty' || field === 'ingredient') {
          const qty = parseFloat(updated.qty) || 0;
          updated.lineCost = calculateLineCost(qty, updated.ingredient);
        }
        return updated;
      })
    );
  };

  const addLine = () => {
    setLines(prev => [
      ...prev,
      {
        id: Date.now(),
        qty: '',
        unit: '',
        ingredient: '',
        lineCost: 0
      }
    ]);
  };

  const removeLine = (id) => {
    setLines(prev => prev.filter(line => line.id !== id));
  };

  const totalCost = lines.reduce((sum, line) => sum + line.lineCost, 0);

  return (
    <div className="p-6 max-w-4xl mx-auto bg-white shadow-lg rounded-lg">
      <h2 className="text-2xl font-semibold mb-6 text-gray-800">Recipe Ingredients</h2>

      {lines.map((line, index) => (
        <div key={line.id} className="grid grid-cols-6 gap-4 items-end mb-4">
          <div>
            <label className="block text-sm text-gray-700">Qty</label>
            <input
              type="number"
              value={line.qty}
              onChange={(e) => handleChange(line.id, 'qty', e.target.value)}
              className="mt-1 w-full border rounded p-2"
            />
          </div>
          <div>
            <label className="block text-sm text-gray-700">Unit</label>
            <input
              type="text"
              value={line.unit}
              onChange={(e) => handleChange(line.id, 'unit', e.target.value)}
              className="mt-1 w-full border rounded p-2"
            />
          </div>
          <div className="col-span-2">
            <label className="block text-sm text-gray-700">Ingredient</label>
            <input
              type="text"
              value={line.ingredient}
              onChange={(e) => handleChange(line.id, 'ingredient', e.target.value)}
              className="mt-1 w-full border rounded p-2"
            />
          </div>
          <div className="text-gray-800 font-semibold mt-6">
            ${line.lineCost.toFixed(2)}
          </div>
          <div className="mt-6">
            <button
              onClick={() => removeLine(line.id)}
              className="text-red-600 text-sm hover:underline"
            >
              Remove
            </button>
          </div>
        </div>
      ))}

      <div className="flex justify-between items-center mt-6">
        <button
          onClick={addLine}
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
        >
          + Add Ingredient
        </button>
        <div className="text-lg font-bold text-gray-800">
          Total Cost: ${totalCost.toFixed(2)}
        </div>
      </div>
    </div>
  );
}
