// src/App.js
import React from "react";
import { Routes, Route } from "react-router-dom";
import RecipeEditor from "./RecipeEditor"; // Corrected path based on your file structure

function App() {
  return (
    <div className="min-h-screen bg-gray-100 text-gray-900 font-satoshi">
      <Routes>
        <Route path="/recipe" element={<RecipeEditor />} />
        <Route
          path="/"
          element={
            <div className="p-8 text-center text-xl font-semibold">
              Welcome to the Recipe Builder
            </div>
          }
        />
        <Route
          path="*"
          element={
            <div className="p-8 text-center text-red-500 font-semibold">
              404 - Page Not Found
            </div>
          }
        />
      </Routes>
    </div>
  );
}

export default App;
