import React, { useState, useContext } from 'react';
import { RecipeContext } from './context/RecipeContext';

function NewRecipeForm() {
  const { addRecipe } = useContext(RecipeContext);
  const [pastedText, setPastedText] = useState('');

  const parseIngredients = (text) => {
    const lines = text.split('\n').map(line => line.trim()).filter(Boolean);

    return lines.map(line => {
      // Remove bullet or parentheses
      const cleaned = line.replace(/^[-•\d\)\(]+/, '').trim();

      // Try to match format: "24 ounces – Ground beef – $" or "1.5 tsp – Salt"
      const parts = cleaned.split(/–|-/).map(p => p.trim());

      const [qtyAndUnit = '', name = '', cost = ''] = parts;

      return {
        name: name || 'Unnamed',
        quantity: qtyAndUnit || '',
        cost: cost || '',
      };
    });
  };

  const handleSubmit = () => {
    const ingredients = parseIngredients(pastedText);

    const newRecipe = {
      name: 'Untitled (Pasted)',
      yield: '',
      ingredients,
    };

    addRecipe(newRecipe);
    setPastedText('');
  };

  return (
    <div className="mb-6">
      <h2 className="font-semibold mb-2">Paste Ingredients</h2>
      <textarea
        value={pastedText}
        onChange={(e) => setPastedText(e.target.value)}
        rows={6}
        className="w-full border p-2 mb-2"
        placeholder="Paste ingredients here, one per line"
      />
      <button
        onClick={handleSubmit}
        className="bg-purple-600 text-white px-4 py-2 rounded hover:bg-purple-700"
      >
        Parse & Save Recipe
      </button>
    </div>
  );
}

export default NewRecipeForm;
