// Dashboard.js
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import {
  MoreVertical, Trash2, FileText, Upload, Printer,
  Apple, BookOpen, Library, HelpCircle, Share2
} from "lucide-react";

function Dashboard() {
  const navigate = useNavigate();
  const [recipes, setRecipes] = useState([]);
  const [menuOpenIndex, setMenuOpenIndex] = useState(null);
  const [selected, setSelected] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [ownerFilter, setOwnerFilter] = useState("All");

  useEffect(() => {
    const keys = Object.keys(localStorage);
    const validRecipes = [];

    keys.forEach((key) => {
      try {
        const val = JSON.parse(localStorage.getItem(key));
        if (val && typeof val.title === "string" && Array.isArray(val.ingredients)) {
          validRecipes.push({
            key,
            title: val.title,
            owner: key.startsWith("personal") ? "Personal" : "First Concept",
            lastUpdated: val.lastUpdated || null
          });
        }
      } catch (err) {
        console.warn(`⚠️ Could not parse ${key}`, err);
      }
    });

    setRecipes(validRecipes);
  }, []);

  const toggleSelect = (key) => {
    setSelected((prev) =>
      prev.includes(key) ? prev.filter((id) => id !== key) : [...prev, key]
    );
  };

  const formatDate = (iso) => {
    if (!iso) return "—";
    return new Date(iso).toLocaleDateString(undefined, {
      year: "numeric", month: "short", day: "numeric"
    });
  };

  const exportSelected = () => {
    selected.forEach((key) => {
      const raw = localStorage.getItem(key);
      if (!raw) return;
      const blob = new Blob([raw], { type: "application/json" });
      const a = document.createElement("a");
      a.href = URL.createObjectURL(blob);
      a.download = `${key}.json`;
      a.click();
    });
  };

  const printSelected = () => {
    const recipesToPrint = selected.map((key) => {
      const raw = localStorage.getItem(key);
      if (!raw) return null;
      const recipe = JSON.parse(raw);
      const totalCost = recipe.ingredients.reduce((sum, ing) => sum + parseFloat(ing.cost || 0), 0);
      const ingredientsHTML = recipe.ingredients.map((ing) => `
        <tr>
          <td>${ing.qty}</td><td>${ing.unit}</td><td>${ing.ingredient}</td>
          <td>${ing.modifier || ""}</td><td>${ing.category} / ${ing.subcategory}</td>
          <td style="text-align:right;">$${parseFloat(ing.cost).toFixed(2)}</td>
        </tr>
      `).join("");

      return `
        <section>
          <h1>${recipe.title}</h1>
          <p><strong>Printed:</strong> ${new Date().toLocaleString()}</p>
          <table>
            <thead><tr>
              <th>Qty</th><th>Unit</th><th>Ingredient</th><th>Modifier</th>
              <th>Category</th><th style="text-align:right;">Cost</th>
            </tr></thead>
            <tbody>${ingredientsHTML}</tbody>
          </table>
          <h2>Total Cost: $${totalCost.toFixed(2)}</h2>
          ${recipe.instructions ? `<h2>Instructions</h2><p>${recipe.instructions}</p>` : ""}
        </section>
      `;
    }).filter(Boolean).join("");

    const html = `
      <html><head><title>Print Recipes</title><style>
        body { font-family: sans-serif; padding: 2rem; }
        table { width: 100%; border-collapse: collapse; margin-top: 1rem; }
        th, td { border: 1px solid #ccc; padding: 4px; font-size: 14px; }
        th { background: #f0f0f0; }
        section { page-break-after: always; margin-bottom: 60px; }
        @media print {
          footer::after { content: "Page " counter(page); }
          footer { position: fixed; bottom: 0; right: 0; font-size: 12px; color: #666; }
        }
      </style></head><body>${recipesToPrint}<footer></footer></body></html>
    `;
    const win = window.open("", "_blank");
    win.document.write(html);
    win.document.close();
    win.print();
  };

  const sendSelected = async () => {
    for (const key of selected) {
      const raw = localStorage.getItem(key);
      if (!raw) continue;
      const file = new File([raw], `${key}.json`, { type: "application/json" });
      if (navigator.canShare?.({ files: [file] })) {
        try {
          await navigator.share({
            title: "Recipe: " + key,
            text: "Here's a recipe from the Recipe Dashboard",
            files: [file]
          });
        } catch (err) {
          console.warn("Share cancelled or not supported", err);
        }
      }
    }
  };

  const deleteSelected = () => {
    if (window.confirm(`Delete ${selected.length} recipe(s)?`)) {
      selected.forEach((key) => localStorage.removeItem(key));
      setRecipes((prev) => prev.filter((r) => !selected.includes(r.key)));
      setSelected([]);
    }
  };

  const handleDelete = (key) => {
    if (window.confirm(`Delete this recipe?`)) {
      localStorage.removeItem(key);
      setRecipes(recipes.filter((r) => r.key !== key));
    }
  };

  const filteredRecipes = recipes.filter((r) =>
    r.title.toLowerCase().includes(searchTerm.toLowerCase()) &&
    (ownerFilter === "All" || r.owner === ownerFilter)
  );

  return (
    <div className="min-h-screen bg-gray-100">
      {/* Filter + Search + Help */}
      <div className="flex justify-between px-6 py-3 bg-white border-b">
        <select value={ownerFilter} onChange={(e) => setOwnerFilter(e.target.value)} className="border px-2 py-1 rounded">
          <option value="All">All</option>
          <option value="Personal">Personal</option>
          <option value="First Concept">First Concept</option>
        </select>
        <div className="flex gap-2">
          <input type="text" placeholder="Search..." value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)} className="border px-2 py-1 rounded text-sm" />
          <HelpCircle className="text-gray-500" />
        </div>
      </div>

      {/* Bulk Actions */}
      {selected.length > 0 && (
        <div className="flex items-center gap-4 px-6 py-2 bg-blue-50 border-b text-sm text-blue-600 font-semibold">
          <input type="checkbox" checked={selected.length === filteredRecipes.length}
            onChange={() => setSelected(selected.length === filteredRecipes.length ? [] : filteredRecipes.map(r => r.key))} />
          <span>{selected.length} Selected</span>
          <FileText onClick={exportSelected} />
          <Share2 onClick={sendSelected} />
          <Printer onClick={printSelected} />
          <Trash2 onClick={deleteSelected} />
        </div>
      )}

      <main className="p-6 space-y-2">
        {filteredRecipes.map((r, i) => (
          <div key={r.key} className={`flex justify-between items-center p-4 bg-white rounded shadow-sm ${selected.includes(r.key) ? "bg-blue-50" : ""}`}>
            <div className="flex items-center gap-2">
              <input type="checkbox" checked={selected.includes(r.key)} onChange={() => toggleSelect(r.key)} />
              <button className="hover:underline font-medium" onClick={() => navigate(`/recipe?title=${encodeURIComponent(r.title)}`)}>{r.title}</button>
            </div>
            <div className="flex items-center gap-3 text-gray-500 text-sm">
              <span className="w-6 h-6 flex items-center justify-center rounded-full bg-black text-white text-xs">
                {r.owner === "Personal" ? "P" : "FC"}
              </span>
              <span>{formatDate(r.lastUpdated)}</span>
              <MoreVertical onClick={() => setMenuOpenIndex(i === menuOpenIndex ? null : i)} />
              {menuOpenIndex === i && (
                <div className="absolute mt-8 right-6 bg-white shadow rounded border w-48 z-10 text-sm">
                  <div onClick={() => handleDelete(r.key)} className="px-4 py-2 hover:bg-red-100 text-red-600 flex gap-2 items-center cursor-pointer">
                    <Trash2 size={16} /> Delete
                  </div>
                </div>
              )}
            </div>
          </div>
        ))}
        {filteredRecipes.length === 0 && (
          <div className="text-center text-sm text-gray-500 py-8">No recipes found.</div>
        )}
      </main>
    </div>
  );
}

export default Dashboard;
