// src/RecipeEditor.js
import React from "react";
import { useLocation, useNavigate } from "react-router-dom";
import IngredientManager from "./IngredientManager";

function RecipeEditor() {
  const location = useLocation();
  const navigate = useNavigate();
  const searchParams = new URLSearchParams(location.search);
  const recipeTitle = searchParams.get("title") || "Untitled Recipe";
  const returnTo = searchParams.get("returnTo");

  const handleReturn = () => {
    if (returnTo) {
      navigate(`/recipe?title=${returnTo}`);
    }
  };

  return (
    <div className="p-6">
      <div className="flex justify-between items-center mb-4">
        <h1 className="text-2xl font-bold">Editing Recipe: {recipeTitle}</h1>
        {returnTo && (
          <button
            onClick={handleReturn}
            className="text-sm text-blue-600 hover:underline"
          >
            ‚Üê Return to {returnTo}
          </button>
        )}
      </div>

      <IngredientManager recipeTitle={recipeTitle} />

      {/* Future: <RecipeCostPanel /> or Save Button */}
    </div>
  );
}

export default RecipeEditor;
