// Full updated RecipeEditor.js with dropdown and fixed subcategoryOptions
import React, { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import IngredientManager from "./IngredientManager";
import { FileText, Apple, BookOpen, Library, MoreVertical } from "lucide-react";

function RecipeEditor() {
  const location = useLocation();
  const navigate = useNavigate();
  const searchParams = new URLSearchParams(location.search);
  const initialTitle = searchParams.get("title") || "Untitled Recipe";
  const returnTo = searchParams.get("returnTo");

  const [recipeTitle, setRecipeTitle] = useState(initialTitle);
  const [instructions, setInstructions] = useState("");
  const [ingredients, setIngredients] = useState([]);
  const [yieldQty, setYieldQty] = useState(10);
  const [scaleFactor, setScaleFactor] = useState(1);
  const [menuOpen, setMenuOpen] = useState(false);

  const unitOptions = ["g", "kg", "oz", "lb", "cup", "tbsp", "tsp", "each"];
  const categoryOptions = [
    "Baking Ingredients", "Dairy & Eggs", "Fermented & Pickled Items", "Flour & Milled Products",
    "Fruits", "Grains & Starches", "Herbs & Spices", "Nuts & Seeds", "Legumes & Beans",
    "Meat & Poultry", "Oils & Vinegars", "Pasta & Noodles", "Sauces & Condiments",
    "Seafood", "Stocks & Broths", "Sweeteners", "Produce", "Garnishes",
    "Prepared", "Bakery Items", "Frozen Items", "Miscellaneous", "Vegetables"
  ];

  const subcategoryOptions = {
    "Baking Ingredients": ["Chocolate", "Flavorings", "Leavening Agents", "Thickeners"],
    "Dairy & Eggs": ["Butter", "Cheese", "Cream", "Milk", "Cultured Dairy", "Whole (Shell) Eggs", "Liquid Eggs"],
    "Fermented & Pickled Items": ["Fermented"],
    "Flour & Milled Products": ["Flours", "Cornmeal", "Semolina"],
    "Fruits": ["Berries", "Citrus Fruits", "Dried Fruits", "Melons", "Stone Fruits", "Tropical Fruits"],
    "Grains & Starches": ["Barley", "Corn", "Couscous", "Farro", "Oats", "Quinoa", "Rice"],
    "Herbs & Spices": ["Dried Spices", "Fresh Herbs"],
    "Nuts & Seeds": ["Nuts", "Seeds"],
    "Legumes & Beans": ["Black Beans", "Chickpeas", "Kidney Beans", "Lentils", "Navy Beans", "Soybeans"],
    "Meat & Poultry": ["Beef", "Chicken", "Duck", "Game Meats", "Lamb", "Pork", "Processed Meats", "Turkey"],
    "Oils & Vinegars": ["Oils", "Vinegars"],
    "Pasta & Noodles": ["Egg Noodles", "Gluten-Free Pasta", "Rice Noodles", "Soba", "Udon"],
    "Sauces & Condiments": ["Common Sauces", "Condiments"],
    "Seafood": ["Finfish", "Mollusks", "Shellfish", "Cured/Smoked Seafood"],
    "Stocks & Broths": ["Stocks"],
    "Sweeteners": ["Natural Sweeteners", "Processed Sugars"],
    "Produce": ["Alliums", "Cruciferous Vegetables", "Leafy Greens", "Legumes", "Nightshades", "Root Vegetables"],
    "Garnishes": ["Finishing Touches", "Garnishes"],
    "Prepared": ["Garnishes & Toppings", "Prepped Proteins", "Prepped Starches", "Prepped Vegetables", "Sauces & Reductions", "Stocks & Broths", "Sub-Recipe"],
    "Bakery Items": ["Breads"],
    "Frozen Items": ["French Fries", "Frozen Fruit and Purees"],
    "Miscellaneous": ["Water"],
    "Vegetables": ["Canned / Preserved"]
  };

  const ingredientOptions = [
    "Milk", "Cheddar Cheese", "Ground Beef", "Chicken Breast",
    "Spinach", "Carrots", "Garlic", "Thyme", "Cinnamon", "Butter"
  ];

    const createEmptyIngredient = () => ({ qty: "", unit: "", category: "", subcategory: "", ingredient: "", modifier: "", cost: "", isSubrecipe: false });

  useEffect(() => {
    const saved = localStorage.getItem(recipeTitle);
    if (saved) {
      try {
        const data = JSON.parse(saved);
        if (data && Array.isArray(data.ingredients)) {
          setIngredients(data.ingredients);
          setInstructions(data.instructions || "");
          setYieldQty(data.yield || 10);
        } else {
          setIngredients([createEmptyIngredient()]);
        }
      } catch {
        setIngredients([createEmptyIngredient()]);
      }
    } else {
      setIngredients([createEmptyIngredient()]);
    }
  }, [recipeTitle]);

  const saveRecipe = () => {
    localStorage.setItem(recipeTitle, JSON.stringify({ title: recipeTitle, ingredients, instructions, yield: yieldQty }));
    alert("Recipe saved!");
  };

  const exportRecipe = () => {
    const blob = new Blob([JSON.stringify({ title: recipeTitle, ingredients, instructions, yield: yieldQty }, null, 2)], { type: "application/json" });
    const link = document.createElement("a");
    link.href = URL.createObjectURL(blob);
    link.download = `${recipeTitle}.json`;
    link.click();
  };

  const duplicateRecipe = () => {
    const newTitle = `${recipeTitle} (Copy)`;
    localStorage.setItem(newTitle, JSON.stringify({ title: newTitle, ingredients, instructions, yield: yieldQty }));
    navigate(`/recipe?title=${encodeURIComponent(newTitle)}`);
  };

  const deleteRecipe = () => {
    if (window.confirm("Delete this recipe?")) {
      localStorage.removeItem(recipeTitle);
      navigate("/");
    }
  };

  const totalCost = ingredients.reduce((sum, ing) => (parseFloat(ing.qty) || 0) * (parseFloat(ing.cost) || 0) * scaleFactor + sum, 0);

  return (
    <div className="min-h-screen bg-gray-100 font-sans">
      <header className="bg-[#14523F] text-white px-6 py-2 shadow">
        <div className="flex flex-col items-start sm:flex-row sm:items-center sm:justify-between">
          <div className="flex items-start gap-3">
            <img src="/ToqueWorks.png" alt="Toque Logo" className="h-28 w-auto object-contain" />
          </div>
          <nav className="flex gap-6 mt-4 sm:mt-0">
            <button onClick={() => navigate("/")} className="text-base font-medium text-white border-b-2 border-white pb-1 flex items-center gap-2">
              <FileText size={20} /> Recipes
            </button>
            <button onClick={() => navigate("/ingredients")} className="text-base font-medium text-white hover:text-gray-200 flex items-center gap-2">
              <Apple size={20} /> Ingredients
            </button>
            <button onClick={() => navigate("/menus")} className="text-base font-medium text-white hover:text-gray-200 flex items-center gap-2">
              <BookOpen size={20} /> Menus
            </button>
            <button onClick={() => navigate("/books")} className="text-base font-medium text-white hover:text-gray-200 flex items-center gap-2">
              <Library size={20} /> Recipe Books
            </button>
          </nav>
        </div>
      </header>

      <div className="max-w-6xl mx-auto mt-8 bg-white shadow rounded-lg p-6 space-y-6 relative">
        <div className="absolute top-6 right-6">
          <button onClick={() => setMenuOpen(!menuOpen)} className="p-2 hover:bg-gray-100 rounded-full">
            <MoreVertical size={20} />
          </button>
          {menuOpen && (
            <div className="absolute right-0 mt-2 w-48 bg-white shadow-lg rounded-md border z-10">
              <ul className="text-sm text-gray-700">
                <li onClick={() => window.print()} className="px-4 py-2 hover:bg-gray-100 cursor-pointer">Print</li>
                <li className="px-4 py-2 text-gray-400 cursor-not-allowed">Share (coming soon)</li>
                <li onClick={duplicateRecipe} className="px-4 py-2 hover:bg-gray-100 cursor-pointer">Duplicate</li>
                <li onClick={deleteRecipe} className="px-4 py-2 text-red-600 hover:bg-red-100 cursor-pointer">Delete</li>
              </ul>
            </div>
          )}
        </div>

        <div className="flex justify-between items-center border-b pb-2">
          <input value={recipeTitle} onChange={(e) => setRecipeTitle(e.target.value)} className="text-2xl font-bold border-b border-green-300 focus:outline-none w-full max-w-lg text-green-900" />
          {returnTo && <button onClick={() => navigate(`/recipe?title=${returnTo}`)} className="text-sm text-blue-600 hover:underline ml-4">‚Üê Return to {returnTo}</button>}
        </div>

        <div>
          <label className="block font-semibold mb-1">Yield</label>
          <div className="flex items-center gap-2">
            <input type="number" value={yieldQty} onChange={(e) => setYieldQty(parseFloat(e.target.value) || 1)} className="w-24 p-2 border rounded" />
            <span>servings</span>
            {[0.5, 1, 2, 4].map((factor) => (
              <button key={factor} onClick={() => setScaleFactor(factor)} className="px-3 py-1 border rounded hover:bg-gray-100">{factor}x</button>
            ))}
          </div>
        </div>

        <IngredientManager
          ingredients={ingredients}
          setIngredients={setIngredients}
          createEmptyIngredient={createEmptyIngredient}
          recipeTitle={recipeTitle}
          unitOptions={unitOptions}
          categoryOptions={categoryOptions}
          subcategoryOptions={subcategoryOptions}
          ingredientOptions={ingredientOptions}
          scaleFactor={scaleFactor}
          showTitle={false}
        />

        <div className="text-right mt-2 font-semibold text-slate-800 text-lg">
          Total Cost: ${totalCost.toFixed(2)}
        </div>

        <div>
          <h3 className="text-lg font-semibold mb-2">Instructions / Method</h3>
          <textarea value={instructions} onChange={(e) => setInstructions(e.target.value)} rows={6} placeholder="Describe how to prepare the dish, step by step." className="w-full p-3 border rounded" />
        </div>

        <div className="flex space-x-4">
          <button onClick={saveRecipe} className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700">Save Recipe</button>
          <button onClick={exportRecipe} className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700">Export JSON</button>
          <label className="cursor-pointer bg-gray-200 px-4 py-2 rounded hover:bg-gray-300">
            Import JSON
            <input type="file" accept="application/json" className="hidden" onChange={(e) => importRecipe(e)} />
          </label>
        </div>
      </div>
    </div>
  );
}

export default RecipeEditor;
