import React from "react";

function PrepInstructions({ instructions }) {
  return (
    <div className="space-y-4 max-w-[800px] w-full">
      <h2 className="text-lg font-semibold">Preparation Instructions</h2>

      {instructions.length === 0 && (
        <p className="text-sm italic text-gray-500">No instructions added yet.</p>
      )}

      {instructions.map((step, index) => (
        <div key={index} className="text-sm">
          <div className="flex items-start gap-2">
            <span className="font-medium text-gray-800 whitespace-nowrap pt-1">
              Step {index + 1}.
            </span>
            <div className="flex-1">
              <p className="font-semibold">{step.title || <em className="text-gray-400">No title</em>}</p>
              <p className="text-gray-800 mt-1">
                {step.description || <em className="text-gray-400">No details provided.</em>}
              </p>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
}

export default PrepInstructions;
