import React, { useContext, useState } from 'react';
import { RecipeContext } from './context/RecipeContext';

export default function NewRecipeForm() {
  const { addRecipe } = useContext(RecipeContext);

  const [recipeName, setRecipeName] = useState('');
  const [recipeYield, setRecipeYield] = useState('');
  const [ingredients, setIngredients] = useState([]);
  const [instructions, setInstructions] = useState([]);

  const [ingredientName, setIngredientName] = useState('');
  const [ingredientQty, setIngredientQty] = useState('');
  const [ingredientCost, setIngredientCost] = useState('');

  const [instructionText, setInstructionText] = useState('');

  const addIngredient = () => {
    if (ingredientName && ingredientQty && ingredientCost) {
      setIngredients((prev) => [
        ...prev,
        { name: ingredientName, quantity: ingredientQty, cost: ingredientCost },
      ]);
      setIngredientName('');
      setIngredientQty('');
      setIngredientCost('');
    }
  };

  const addInstruction = () => {
    if (instructionText) {
      setInstructions((prev) => [...prev, instructionText]);
      setInstructionText('');
    }
  };

  const saveRecipe = () => {
    if (!recipeName) return alert('Enter recipe name.');
    addRecipe({ name: recipeName, yield: recipeYield, ingredients, instructions });
    setRecipeName('');
    setRecipeYield('');
    setIngredients([]);
    setInstructions([]);
  };

  return (
    <div className="space-y-4">
      <input
        className="w-full p-2 border"
        placeholder="Recipe Name"
        value={recipeName}
        onChange={(e) => setRecipeName(e.target.value)}
      />
      <input
        className="w-full p-2 border"
        placeholder="Yield (portions)"
        value={recipeYield}
        onChange={(e) => setRecipeYield(e.target.value)}
      />

      <div>
        <h2 className="font-semibold">Add Ingredient</h2>
        <div className="flex gap-2 mb-2">
          <input
            className="flex-1 p-2 border"
            placeholder="Name"
            value={ingredientName}
            onChange={(e) => setIngredientName(e.target.value)}
          />
          <input
            className="w-20 p-2 border"
            placeholder="Qty"
            value={ingredientQty}
            onChange={(e) => setIngredientQty(e.target.value)}
          />
          <input
            className="w-24 p-2 border"
            placeholder="Cost"
            value={ingredientCost}
            onChange={(e) => setIngredientCost(e.target.value)}
          />
        </div>
        <button
          onClick={addIngredient}
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
        >
          Add Ingredient
        </button>

        <ul className="mt-2 list-disc pl-5 text-sm text-gray-700">
          {ingredients.map((ing, i) => (
            <li key={i}>
              {ing.name} – {ing.quantity} – ${ing.cost}
            </li>
          ))}
        </ul>
      </div>

      <div>
        <h2 className="font-semibold">Instructions</h2>
        <div className="flex gap-2 mb-2">
          <input
            className="flex-1 p-2 border"
            placeholder="Step description"
            value={instructionText}
            onChange={(e) => setInstructionText(e.target.value)}
          />
          <button
            onClick={addInstruction}
            className="bg-purple-600 text-white px-4 py-2 rounded hover:bg-purple-700"
          >
            Add Step
          </button>
        </div>
        <ol className="pl-5 list-decimal text-sm text-gray-700">
          {instructions.map((step, i) => (
            <li key={i}>{step}</li>
          ))}
        </ol>
      </div>

      <button
        onClick={saveRecipe}
        className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
      >
        Save Recipe
      </button>
    </div>
  );
}
