import React, { useState, useContext } from 'react';
import { RecipeContext } from './context/RecipeContext';

function NewRecipeForm({ onClose }) {
  const { addRecipe } = useContext(RecipeContext);

  const [rawIngredients, setRawIngredients] = useState('');
  const [rawMethod, setRawMethod] = useState('');

  const handleSave = () => {
    const newRecipe = {
      name: 'Untitled (Pasted)', // Placeholder until parsing added
      yield: '',
      ingredients: [],
      method: rawMethod,
      rawIngredients,
    };
    addRecipe(newRecipe);
    onClose();
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white w-full max-w-4xl p-6 rounded shadow-lg relative">
        <button
          className="absolute top-4 right-4 text-2xl font-bold"
          onClick={onClose}
        >
          ×
        </button>
        <h2 className="text-2xl font-bold mb-2">Add Ingredients & Prep Method</h2>
        <p className="text-gray-600 mb-4">
          Type or copy/paste ingredients and/or prep steps from any word doc, spreadsheet, PDF, or website.
        </p>

        <div className="grid grid-cols-2 gap-4">
          <div>
            <h3 className="font-semibold mb-1">Ingredients</h3>
            <textarea
              className="w-full h-64 p-2 border bg-blue-50 text-blue-900"
              placeholder="Dry Mix:\n500g flour\n1/2c semolina\nsalt to taste\n\nWet:\n5 cloves Garlic\n3 egg yolks\nOlive Oil (room temp)"
              value={rawIngredients}
              onChange={(e) => setRawIngredients(e.target.value)}
            />
            <p className="text-xs text-gray-500 mt-1">
              Add notes to ingredients <span className="font-mono">(note)</span> — Use headers like <span className="font-mono">To Garnish:</span>
            </p>
          </div>

          <div>
            <h3 className="font-semibold mb-1">Prep Method</h3>
            <textarea
              className="w-full h-64 p-2 border bg-blue-50 text-blue-900"
              placeholder="Dry Mix:\nHeat oven to 350f\nCombine dry ingredients in medium bowl\n\nGarnish:\nAdd chopped chocolate chips as garnish"
              value={rawMethod}
              onChange={(e) => setRawMethod(e.target.value)}
            />
            <p className="text-xs text-gray-500 mt-1">
              Add notes to prep steps <span className="font-mono">(note)</span> — Use headers like <span className="font-mono">To Garnish:</span>
            </p>
          </div>
        </div>

        <div className="mt-6 flex justify-between">
          <button
            onClick={onClose}
            className="text-indigo-500 hover:underline"
          >
            Skip
          </button>
          <button
            onClick={handleSave}
            className="bg-indigo-500 text-white px-6 py-2 rounded hover:bg-indigo-600"
          >
            Add Recipe
          </button>
        </div>
      </div>
    </div>
  );
}

export default NewRecipeForm;
