// src/components/PrepInstructions.jsx
import React from "react";

function PrepInstructions({ instructions, setInstructions }) {
  const updateStep = (index, field, value) => {
    const updated = [...instructions];
    updated[index][field] = value;
    setInstructions(updated);
  };

  const addStep = () => {
    setInstructions(prev => [
      ...prev,
      {
        stepNumber: prev.length + 1,
        title: "",
        description: "",
        isCCP: false,
      },
    ]);
  };

  const removeStep = (index) => {
    const updated = instructions
      .filter((_, i) => i !== index)
      .map((step, i) => ({ ...step, stepNumber: i + 1 }));
    setInstructions(updated);
  };

  return (
    <div className="space-y-6">
      <h2 className="text-xl font-bold">Preparation Instructions</h2>
      {instructions.map((step, index) => (
        <div
          key={index}
          className="border border-gray-300 rounded-lg p-4 bg-white shadow-sm"
        >
          <div className="flex items-center justify-between mb-2">
            <h3 className="text-lg font-semibold text-gray-800">
              Step {index + 1}
            </h3>
            <div className="flex items-center gap-4">
              <label className="flex items-center text-sm text-red-600 font-medium">
                <input
                  type="checkbox"
                  checked={step.isCCP || false}
                  onChange={(e) =>
                    updateStep(index, "isCCP", e.target.checked)
                  }
                  className="mr-2"
                />
                CCP
              </label>
              {instructions.length > 1 && (
                <button
                  onClick={() => removeStep(index)}
                  className="text-sm text-gray-500 hover:text-red-500"
                >
                  ❌ Remove
                </button>
              )}
            </div>
          </div>

          <input
            type="text"
            value={step.title}
            onChange={(e) => updateStep(index, "title", e.target.value)}
            placeholder="Step Title (e.g., Sear the Chicken)"
            className="w-full p-2 border border-gray-300 rounded mb-2"
          />

          <textarea
            value={step.description}
            onChange={(e) =>
              updateStep(index, "description", e.target.value)
            }
            placeholder="Step Detail (e.g., Heat olive oil and sear on both sides...)"
            rows={3}
            className="w-full p-2 border border-gray-300 rounded"
          />
        </div>
      ))}

      <button
        onClick={addStep}
        className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
      >
        ➕ Add Step
      </button>

      {/* Live Preview */}
      <div className="mt-10">
        <h2 className="text-lg font-semibold mb-2">Live Preview</h2>
        <ol className="list-decimal space-y-4 ml-6">
          {instructions.map((step, index) => (
            <li key={index}>
              <p className="font-bold">
                {step.title}
                {step.isCCP && (
                  <span className="text-red-600 font-medium"> [CCP]</span>
                )}
              </p>
              <p>{step.description}</p>
            </li>
          ))}
        </ol>
      </div>
    </div>
  );
}

export default PrepInstructions;
