// src/components/PrepInstructions.jsx
import React, { useState } from "react";
import InstructionModal from "./InstructionModal";

function PrepInstructions({ instructions, setInstructions }) {
  const [editingIndex, setEditingIndex] = useState(null);

  const handleEdit = (index) => {
    setEditingIndex(index);
  };

  const closeModal = () => {
    setEditingIndex(null);
  };

  const updateInstruction = (index, updated) => {
    const updatedList = [...instructions];
    updatedList[index] = updated;
    setInstructions(updatedList);
  };

  return (
    <div className="space-y-4 max-w-[800px] w-full">
      <h2 className="text-lg font-semibold">Preparation Instructions</h2>

      {instructions.length === 0 && (
        <p className="text-sm italic text-gray-500">No instructions added yet.</p>
      )}

      {instructions.map((step, index) => (
        <button
          key={index}
          onClick={() => handleEdit(index)}
          className="text-left w-full text-sm leading-tight hover:bg-gray-50 px-1 py-2 rounded"
        >
          <div className="flex items-baseline gap-2">
            <span className="font-semibold text-gray-800 whitespace-nowrap w-6 text-right">
              {index + 1}.
            </span>
            <p className="font-semibold text-gray-900">{step.title || <em className="text-gray-400">No title</em>}</p>
          </div>
          <div className="ml-6 mt-0.5">
            <p className="text-gray-800">{step.description || <em className="text-gray-400">No details provided.</em>}</p>
          </div>
        </button>
      ))}

      {editingIndex !== null && (
        <InstructionModal
          step={instructions[editingIndex]}
          onSave={(updated) => {
            updateInstruction(editingIndex, updated);
            closeModal();
          }}
          onCancel={closeModal}
        />
      )}
    </div>
  );
}

export default PrepInstructions;
