import React, { useState, useEffect } from "react";

function RecipeDetailsModal({ title, source, portions, menuPrice, onSave, onCancel }) {
  const [local, setLocal] = useState({ title, source, portions, menuPrice });

  useEffect(() => {
    setLocal({ title, source, portions, menuPrice });
  }, [title, source, portions, menuPrice]);

  const handleChange = (field, value) => {
    setLocal((prev) => ({ ...prev, [field]: value }));
  };

  const handleSubmit = () => {
    onSave({
      title: local.title.trim() || "Untitled Recipe",
      source: local.source.trim(),
      portions: parseInt(local.portions) || 1,
      menuPrice: parseFloat(local.menuPrice) || 0,
    });
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50">
      <div className="bg-white p-6 rounded-lg shadow-lg w-[500px] space-y-4">
        <h2 className="text-lg font-bold">Edit Recipe Details</h2>

        <div className="space-y-3">
          <div>
            <label className="block text-sm font-medium text-gray-700">Recipe Title</label>
            <input
              value={local.title}
              onChange={(e) => handleChange("title", e.target.value)}
              className="w-full border p-2 rounded"
              placeholder="e.g., Chicken Marsala"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700">Source</label>
            <input
              value={local.source}
              onChange={(e) => handleChange("source", e.target.value)}
              className="w-full border p-2 rounded"
              placeholder="e.g., Culinary Fundamentals"
            />
          </div>

          <div className="flex gap-4">
            <div className="flex-1">
              <label className="block text-sm font-medium text-gray-700">Yield Portions</label>
              <input
                type="number"
                value={local.portions}
                onChange={(e) => handleChange("portions", e.target.value)}
                className="w-full border p-2 rounded"
              />
            </div>

            <div className="flex-1">
              <label className="block text-sm font-medium text-gray-700">Menu Price ($)</label>
              <input
                type="number"
                step="0.01"
                value={local.menuPrice}
                onChange={(e) => handleChange("menuPrice", e.target.value)}
                className="w-full border p-2 rounded"
              />
            </div>
          </div>
        </div>

        <div className="flex justify-end gap-2 pt-4">
          <button
            onClick={onCancel}
            className="px-4 py-2 bg-gray-300 hover:bg-gray-400 rounded"
          >
            Cancel
          </button>
          <button
            onClick={handleSubmit}
            className="px-4 py-2 bg-green-600 text-white hover:bg-green-700 rounded"
          >
            Save
          </button>
        </div>
      </div>
    </div>
  );
}

export default RecipeDetailsModal;
