import React, { useState, useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";

const IngredientRow = ({ ingredient, index, updateIngredient, removeIngredient }) => {
  const [localIngredient, setLocalIngredient] = useState(ingredient);
  const navigate = useNavigate();
  const location = useLocation();

  // Extract returnTo from query string if present
  const searchParams = new URLSearchParams(location.search);
  const returnTo = searchParams.get("title") || "Untitled Recipe";

  useEffect(() => {
    setLocalIngredient(ingredient);
  }, [ingredient]);

  const handleChange = (field, value) => {
    const updated = { ...localIngredient, [field]: value };
    setLocalIngredient(updated);
    updateIngredient(index, updated);
  };

  const handleSubrecipeRedirect = () => {
    const subrecipeName = localIngredient.ingredient?.trim();
    if (!subrecipeName) return;

    const queryParams = new URLSearchParams({
      title: subrecipeName,
      returnTo: returnTo,
    });

    navigate(`/recipe?${queryParams.toString()}`);
  };

  const handleBlur = () => {
    if (localIngredient.isSubrecipe && localIngredient.ingredient?.trim()) {
      handleSubrecipeRedirect();
    }
  };

  return (
    <div className="grid grid-cols-8 gap-2 items-center py-1 border-b text-sm">
      <input
        className="col-span-1 px-2 py-1 border rounded"
        placeholder="Qty"
        value={localIngredient.qty || ""}
        onChange={(e) => handleChange("qty", e.target.value)}
      />
      <input
        className="col-span-1 px-2 py-1 border rounded"
        placeholder="Unit"
        value={localIngredient.unit || ""}
        onChange={(e) => handleChange("unit", e.target.value)}
      />
      <input
        className="col-span-1 px-2 py-1 border rounded"
        placeholder="Category"
        value={localIngredient.category || ""}
        onChange={(e) => handleChange("category", e.target.value)}
      />
      <input
        className="col-span-1 px-2 py-1 border rounded"
        placeholder="Subcategory"
        value={localIngredient.subcategory || ""}
        onChange={(e) => handleChange("subcategory", e.target.value)}
      />
      <input
        className="col-span-2 px-2 py-1 border rounded"
        placeholder="Ingredient"
        value={localIngredient.ingredient || ""}
        onChange={(e) => handleChange("ingredient", e.target.value)}
        onBlur={handleBlur}
      />
      <input
        className="col-span-1 px-2 py-1 border rounded"
        placeholder="Modifier"
        value={localIngredient.modifier || ""}
        onChange={(e) => handleChange("modifier", e.target.value)}
      />
      <input
        className="col-span-1 px-2 py-1 border rounded text-right"
        placeholder="Cost"
        value={localIngredient.cost || ""}
        onChange={(e) => handleChange("cost", e.target.value)}
      />
      <div className="col-span-1 flex items-center space-x-2">
        <label className="text-xs">
          <input
            type="checkbox"
            checked={localIngredient.isSubrecipe || false}
            onChange={(e) => handleChange("isSubrecipe", e.target.checked)}
          />{" "}
          Subrecipe
        </label>
        <button
          className="text-red-500 hover:text-red-700 text-xs"
          onClick={() => removeIngredient(index)}
        >
          âœ•
        </button>
      </div>
    </div>
  );
};

export default IngredientRow;
