// HomePage.js
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

function HomePage() {
  const navigate = useNavigate();
  const [recipes, setRecipes] = useState([]);

  useEffect(() => {
    // Load all recipes from localStorage
    const allKeys = Object.keys(localStorage);
    const recipeList = allKeys.map((key) => {
      try {
        const data = JSON.parse(localStorage.getItem(key));
        if (Array.isArray(data)) {
          return {
            title: key,
            lastViewed: new Date().toLocaleDateString(), // placeholder
            owner: "TO",
          };
        }
      } catch (e) {
        return null;
      }
      return null;
    }).filter(Boolean);

    setRecipes(recipeList);
  }, []);

  const handleNewRecipe = () => {
    const newTitle = prompt("Enter recipe name:", "Untitled Recipe");
    if (newTitle) {
      navigate(`/recipe?title=${encodeURIComponent(newTitle)}`);
    }
  };

  return (
    <div className="p-8 space-y-6">
      <div className="flex justify-between items-center">
        <div className="space-x-4">
          <button className="bg-blue-100 text-blue-800 px-4 py-2 rounded font-medium">Recipes</button>
          <button className="bg-gray-100 text-gray-800 px-4 py-2 rounded">Ingredients</button>
          <button className="bg-gray-100 text-gray-500 px-4 py-2 rounded" disabled>Menus 🔒</button>
          <button className="bg-gray-100 text-gray-800 px-4 py-2 rounded">Recipe Books</button>
        </div>
        <button
          onClick={handleNewRecipe}
          className="bg-blue-600 text-white px-5 py-2 rounded hover:bg-blue-700"
        >
          + New
        </button>
      </div>

      <div className="border rounded-lg overflow-hidden">
        <table className="w-full text-left">
          <thead className="bg-gray-100 text-gray-600 uppercase text-sm">
            <tr>
              <th className="px-4 py-2">Type</th>
              <th className="px-4 py-2">Name</th>
              <th className="px-4 py-2">Owner</th>
              <th className="px-4 py-2">Last Viewed</th>
              <th className="px-4 py-2"></th>
            </tr>
          </thead>
          <tbody>
            {recipes.map((recipe, index) => (
              <tr key={index} className="border-t hover:bg-gray-50">
                <td className="px-4 py-2">📄</td>
                <td
                  className="px-4 py-2 text-blue-700 hover:underline cursor-pointer"
                  onClick={() => navigate(`/recipe?title=${encodeURIComponent(recipe.title)}`)}
                >
                  {recipe.title}
                </td>
                <td className="px-4 py-2">{recipe.owner}</td>
                <td className="px-4 py-2">{recipe.lastViewed}</td>
                <td className="px-4 py-2 text-right">⋮</td>
              </tr>
            ))}
            {recipes.length === 0 && (
              <tr>
                <td colSpan="5" className="px-4 py-6 text-center text-gray-400">
                  No recipes found.
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default HomePage;
