import React, { useState } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import RecipeCostPanel from './RecipeCostPanel';

const ingredientsList = ['Flour', 'Sugar', 'Butter', 'Eggs', 'Milk'];

export default function RecipeEditor() {
  const [title, setTitle] = useState('');
  const [source, setSource] = useState("Tim O'Donnell, MBA, CEC, CCE");
  const [customSource, setCustomSource] = useState('');
  const [useCustomSource, setUseCustomSource] = useState(false);
  const [yieldAmount, setYieldAmount] = useState(32);
  const [yieldUnit, setYieldUnit] = useState('portion');

  const [ingredientLines, setIngredientLines] = useState([
    { qty: '', unit: 'g', ingredient: '', modifier: '', cost: '' },
  ]);

  const [units, setUnits] = useState([
    'g', 'oz', 'ea', 'qts', 'lb', 'ml', 'cup', 'tbsp', 'tsp',
  ]);
  const [showUnitManager, setShowUnitManager] = useState(false);

  const predefinedSources = [
    "Tim O'Donnell, MBA, CEC, CCE",
    'Chef Holland',
    'Culinary Team',
    'Guest Instructor',
    'Other...'
  ];

  const handleChange = (index, field, value) => {
    const updated = [...ingredientLines];
    updated[index][field] = value;
    setIngredientLines(updated);
  };

  const handleUnitChange = (index, value) => {
    handleChange(index, 'unit', value);
  };

  const addIngredientLine = () => {
    setIngredientLines([...ingredientLines, { qty: '', unit: 'g', ingredient: '', modifier: '', cost: '' }]);
  };

  const removeIngredientLine = (index) => {
    const updated = ingredientLines.filter((_, i) => i !== index);
    setIngredientLines(updated);
  };

  const convertUnit = (qty, unit) => {
    if (!qty || isNaN(qty)) return null;
    const q = parseFloat(qty);
    if (unit === 'g') return `${(q * 0.0353).toFixed(2)} oz`;
    if (unit === 'oz') return `${(q * 28.35).toFixed(0)} g`;
    return null;
  };

  const updateUnit = (index) => {
    const newUnit = prompt('Edit unit:', units[index]);
    if (newUnit && newUnit !== units[index]) {
      const updated = [...units];
      updated[index] = newUnit;
      setUnits(updated);
    }
  };

  const deleteUnit = (index) => {
    const updated = [...units];
    updated.splice(index, 1);
    setUnits(updated);
  };

  const handleDragEnd = (result) => {
    if (!result.destination) return;
    const items = Array.from(units);
    const [reordered] = items.splice(result.source.index, 1);
    items.splice(result.destination.index, 0, reordered);
    setUnits(items);
  };

  const totalCost = ingredientLines.reduce((sum, line) => {
    const cost = parseFloat(line.cost);
    return sum + (isNaN(cost) ? 0 : cost);
  }, 0);

  return (
    <div className="max-w-4xl mx-auto p-4 font-satoshi text-gray-800">
      <div className="mb-6">
        <input
          type="text"
          placeholder="Untitled Recipe"
          className="text-3xl font-bold w-full border-none bg-transparent focus:outline-none pl-[5px]"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        {!useCustomSource ? (
          <select
            className="mt-2 text-[15px] font-light w-[384px] border-b border-gray-200 focus:outline-none pb-1 pl-[5px] leading-none bg-transparent text-gray-500"
            value={source}
            onChange={(e) => {
              if (e.target.value === 'Other...') {
                setUseCustomSource(true);
                setCustomSource('');
              } else {
                setSource(e.target.value);
              }
            }}
          >
            {predefinedSources.map((src) => (
              <option key={src} value={src}>
                {src}
              </option>
            ))}
          </select>
        ) : (
          <input
            type="text"
            placeholder="Enter source name"
            className="mt-2 text-[15px] font-light text-gray-500 w-[384px] border-b border-gray-200 focus:outline-none pb-1 pl-[5px] leading-none"
            value={customSource}
            onChange={(e) => {
              setCustomSource(e.target.value);
              setSource(e.target.value);
            }}
            onBlur={() => {
              if (customSource.trim() === '') {
                setUseCustomSource(false);
                setSource(predefinedSources[0]);
              }
            }}
          />
        )}
      </div>

      <div className="flex gap-2 mt-4 mb-4">
        <label className="text-sm font-medium mt-[5px]">Total Yield:</label>
        <input
          type="number"
          className="w-[100px] border-b border-gray-200 focus:outline-none text-sm pl-2"
          value={yieldAmount}
          onChange={(e) => setYieldAmount(e.target.value)}
        />
        <select
          className="text-sm border-b border-gray-200 focus:outline-none pl-2 text-gray-500"
          value={yieldUnit}
          onChange={(e) => setYieldUnit(e.target.value)}
        >
          <option value="portion">portion</option>
          <option value="g">g</option>
          <option value="oz">oz</option>
        </select>
      </div>

      {ingredientLines.map((line, index) => (
        <div
          key={index}
          className="flex flex-wrap items-end gap-x-2 gap-y-1 mb-2 pb-1 border-b border-gray-200"
        >
          <input
            type="number"
            placeholder="Qty"
            className="text-[15px] w-[72px] border-none border-b border-gray-400 focus:outline-none underline font-medium text-gray-900 pl-[5px]"
            value={line.qty}
            onChange={(e) => handleChange(index, 'qty', e.target.value)}
          />
          <select
            value={line.unit}
            onChange={(e) => handleUnitChange(index, e.target.value)}
            className="text-[15px] w-[72px] border-none border-b border-gray-400 bg-transparent focus:outline-none underline font-medium text-gray-900 pl-[5px]"
          >
            {units.map((u) => (
              <option key={u} value={u}>
                {u}
              </option>
            ))}
          </select>
          <span className="text-[15px] text-gray-500 min-w-[70px] pl-[5px]">
            {line.qty && convertUnit(line.qty, line.unit)}
          </span>
          <select
            value={line.ingredient}
            onChange={(e) => handleChange(index, 'ingredient', e.target.value)}
            className="text-[15px] w-[264px] border-none border-b border-gray-300 bg-transparent focus:outline-none font-semibold text-gray-900 pl-[5px]"
          >
            <option value="" disabled>
              Ingredient
            </option>
            {ingredientsList.map((ing) => (
              <option key={ing} value={ing}>
                {ing}
              </option>
            ))}
          </select>
          <input
            type="text"
            placeholder="Modifier (e.g., Instant)"
            className="text-[15px] italic text-gray-500 border-none border-b border-gray-200 bg-transparent focus:outline-none w-[144px] pl-[5px]"
            value={line.modifier}
            onChange={(e) => handleChange(index, 'modifier', e.target.value)}
          />
          <input
            type="number"
            placeholder="Cost"
            className="text-[15px] w-[90px] border-none border-b border-gray-200 bg-transparent focus:outline-none text-gray-500 pl-[5px]"
            value={line.cost}
            onChange={(e) => handleChange(index, 'cost', e.target.value)}
          />
          <button
            onClick={() => removeIngredientLine(index)}
            className="text-xs text-red-500 hover:text-red-700 pl-[5px]"
          >
            Remove
          </button>
        </div>
      ))}

      <div className="mt-4 flex items-center gap-4">
        <button
          onClick={addIngredientLine}
          className="px-4 py-1 text-sm bg-blue-600 text-white rounded hover:bg-blue-700 transition"
        >
          + Add Ingredient
        </button>
        <button
          onClick={() => setShowUnitManager((prev) => !prev)}
          className="px-3 py-1 text-sm text-gray-700 hover:underline"
        >
          ⚙️ Manage Units
        </button>
        <button
          onClick={() => window.print()}
          className="px-3 py-1 text-sm text-gray-700 border border-gray-300 rounded hover:bg-gray-100"
        >
          🖨️ Print
        </button>
      </div>

      {showUnitManager && (
        <div className="mt-6 p-4 border rounded bg-gray-50">
          <h3 className="text-lg font-semibold mb-2">Manage Units</h3>
          <DragDropContext onDragEnd={handleDragEnd}>
            <Droppable droppableId="units">
              {(provided) => (
                <ul
                  className="space-y-2"
                  {...provided.droppableProps}
                  ref={provided.innerRef}
                >
                  {units.map((unit, index) => (
                    <Draggable key={unit} draggableId={unit} index={index}>
                      {(provided) => (
                        <li
                          className="flex items-center gap-2 bg-white p-2 shadow-sm rounded"
                          ref={provided.innerRef}
                          {...provided.draggableProps}
                          {...provided.dragHandleProps}
                        >
                          <span className="flex-1 text-sm">{unit}</span>
                          <button
                            className="text-blue-500 text-xs"
                            onClick={() => updateUnit(index)}
                          >
                            Edit
                          </button>
                          <button
                            className="text-red-500 text-xs"
                            onClick={() => deleteUnit(index)}
                          >
                            Delete
                          </button>
                        </li>
                      )}
                    </Draggable>
                  ))}
                  {provided.placeholder}
                </ul>
              )}
            </Droppable>
          </DragDropContext>
        </div>
      )}

      <RecipeCostPanel
        ingredients={ingredientLines.map((line) => ({
          name: line.ingredient || 'Unnamed',
          cost: parseFloat(line.cost) || 0,
        }))}
        totalYield={parseFloat(yieldAmount) || 0}
        totalCost={totalCost}
      />
    </div>
  );
}
