import React, { useState } from 'react';
import ingredientsDB from './Ingredients';
import ingredientCategories from './IngredientCategories';

export default function IngredientRow({ index, line, onChange, onRemove }) {
  const [showAddForm, setShowAddForm] = useState(false);
  const [newIngredient, setNewIngredient] = useState({
    name: '',
    apCost: '',
    packSize: '',
    packUnit: 'lb',
    yield: 1.0,
    isSubrecipe: false
  });

  const handleChange = (field, value) => {
    onChange(index, field, value);
  };

  const categories = [...new Set(ingredientCategories.map((i) => i.category))];
  const subcategories =
    ingredientCategories.find((cat) => cat.category === line.category)?.subcategories || [];

  const filteredIngredients = ingredientsDB.filter(
    (i) =>
      (!line.subcategory || i.subcategory === line.subcategory) &&
      i.name.toLowerCase().includes(line.ingredientId?.toLowerCase() || '')
  );

  const ingredientOptions = filteredIngredients.map((ing) => ({
    value: ing.id,
    label: ing.isSubrecipe ? `${ing.name} (Subrecipe)` : ing.name
  }));

  const selectedIngredient = ingredientsDB.find((i) => i.id === line.ingredientId);
  const cost = selectedIngredient ? `$${(selectedIngredient.apCost || 0).toFixed(2)}` : '';

  const handleAddIngredient = () => {
    const id = `new_${Date.now()}`;
    const newIng = {
      id,
      name: newIngredient.name,
      category: line.category,
      subcategory: line.subcategory,
      apCost: parseFloat(newIngredient.apCost),
      packSize: parseFloat(newIngredient.packSize),
      packUnit: newIngredient.packUnit,
      yield: parseFloat(newIngredient.yield),
      isSubrecipe: newIngredient.isSubrecipe
    };

    ingredientsDB.push(newIng);
    handleChange('ingredientId', id);
    setShowAddForm(false);
  };

  const showAddOption =
    line.ingredientId &&
    !ingredientsDB.some((ing) => ing.name.toLowerCase() === line.ingredientId.toLowerCase());

  return (
    <div className="flex flex-col gap-1 mb-2">
      <div className="flex gap-2 items-center">
        <input
          type="number"
          placeholder="Qty"
          className="w-[54px] h-[28px] text-sm text-[#6B7280] border-b border-[#A5A9C1] pl-[5px]"
          value={line.qty}
          onChange={(e) => handleChange('qty', e.target.value)}
        />

        <select
          className="w-[70px] h-[28px] text-sm text-[#6B7280] border-b border-[#A5A9C1] pl-[5px]"
          value={line.unit}
          onChange={(e) => handleChange('unit', e.target.value)}
        >
          <option value="g">g</option>
          <option value="oz">oz</option>
          <option value="ea">ea</option>
          <option value="qts">qts</option>
        </select>

        <select
          className="w-[180px] h-[28px] text-sm text-[#6B7280] border-b border-[#A5A9C1] pl-[5px]"
          value={line.category || ''}
          onChange={(e) => handleChange('category', e.target.value)}
        >
          <option value="">Select Category</option>
          {categories.map((cat) => (
            <option key={cat} value={cat}>{cat}</option>
          ))}
        </select>

        <select
          className="w-[180px] h-[28px] text-sm text-[#6B7280] border-b border-[#A5A9C1] pl-[5px]"
          value={line.subcategory || ''}
          onChange={(e) => handleChange('subcategory', e.target.value)}
        >
          <option value="">Select Subcategory</option>
          {subcategories.map((sub) => (
            <option key={sub} value={sub}>{sub}</option>
          ))}
        </select>

        <input
          type="text"
          placeholder="Select Ingredient"
          className="w-[180px] h-[28px] text-sm text-[#6B7280] border-b border-[#A5A9C1] pl-[5px]"
          value={line.ingredientId || ''}
          onChange={(e) => handleChange('ingredientId', e.target.value)}
          onFocus={() => setShowAddForm(false)}
        />

        <input
          type="text"
          placeholder="Modifier"
          className="w-[150px] h-[28px] text-sm text-[#4D5481] border-b border-[#A5A9C1] pl-[5px]"
          value={line.modifier || ''}
          onChange={(e) => handleChange('modifier', e.target.value)}
        />

        <span className="text-sm text-[#6B7280] w-[80px]">{cost}</span>

        <button
          className="text-red-500 hover:text-red-700 text-sm ml-1"
          onClick={() => onRemove(index)}
        >
          Remove
        </button>
      </div>

      {showAddOption && !showAddForm && (
        <button
          className="text-blue-600 text-sm ml-[5px] mt-1 text-left"
          onClick={() => {
            setShowAddForm(true);
            setNewIngredient({ ...newIngredient, name: line.ingredientId });
          }}
        >
          Ingredient not found – Add it?
        </button>
      )}

      {showAddForm && (
        <div className="ml-[5px] mt-1 border border-gray-200 p-3 rounded bg-white shadow-sm space-y-2 w-full max-w-xl">
          <div className="grid grid-cols-2 gap-3">
            <div>
              <label className="block text-xs text-gray-500">Name</label>
              <input
                className="w-full border border-gray-300 text-sm pl-2 h-[28px]"
                value={newIngredient.name}
                onChange={(e) =>
                  setNewIngredient({ ...newIngredient, name: e.target.value })
                }
              />
            </div>
            <div>
              <label className="block text-xs text-gray-500">AP Cost ($)</label>
              <input
                type="number"
                className="w-full border border-gray-300 text-sm pl-2 h-[28px]"
                value={newIngredient.apCost}
                onChange={(e) =>
                  setNewIngredient({ ...newIngredient, apCost: e.target.value })
                }
              />
            </div>
            <div>
              <label className="block text-xs text-gray-500">Pack Size</label>
              <input
                type="number"
                className="w-full border border-gray-300 text-sm pl-2 h-[28px]"
                value={newIngredient.packSize}
                onChange={(e) =>
                  setNewIngredient({ ...newIngredient, packSize: e.target.value })
                }
              />
            </div>
            <div>
              <label className="block text-xs text-gray-500">Pack Unit</label>
              <select
                className="w-full border border-gray-300 text-sm h-[28px] pl-2"
                value={newIngredient.packUnit}
                onChange={(e) =>
                  setNewIngredient({ ...newIngredient, packUnit: e.target.value })
                }
              >
                <option value="lb">lb</option>
                <option value="oz">oz</option>
                <option value="ea">ea</option>
                <option value="qt">qt</option>
                <option value="gal">gal</option>
              </select>
            </div>
            <div>
              <label className="block text-xs text-gray-500">Yield (0–1)</label>
              <input
                type="number"
                className="w-full border border-gray-300 text-sm pl-2 h-[28px]"
                value={newIngredient.yield}
                onChange={(e) =>
                  setNewIngredient({ ...newIngredient, yield: e.target.value })
                }
              />
            </div>
            <div className="flex items-center mt-5">
              <input
                type="checkbox"
                id={`subrecipe-${index}`}
                className="mr-2"
                checked={newIngredient.isSubrecipe}
                onChange={(e) =>
                  setNewIngredient({ ...newIngredient, isSubrecipe: e.target.checked })
                }
              />
              <label htmlFor={`subrecipe-${index}`} className="text-sm text-gray-600">
                This is a subrecipe
              </label>
            </div>
          </div>

          <button
            className="mt-2 px-3 py-1 bg-blue-600 text-white rounded text-sm hover:bg-blue-700"
            onClick={handleAddIngredient}
          >
            Add Ingredient
          </button>
        </div>
      )}
    </div>
  );
}
