import React, { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import IngredientManager from "./IngredientManager";

function RecipeEditor() {
  const location = useLocation();
  const navigate = useNavigate();
  const searchParams = new URLSearchParams(location.search);
  const initialTitle = searchParams.get("title") || "Untitled Recipe";
  const returnTo = searchParams.get("returnTo");

  const [recipeTitle, setRecipeTitle] = useState(initialTitle);
  const [ingredients, setIngredients] = useState([]);
  const [yieldAmount, setYieldAmount] = useState(10);
  const [instructions, setInstructions] = useState("");

  const unitOptions = ["g", "kg", "oz", "lb", "cup", "tbsp", "tsp", "each"];

  const categoryOptions = [ /* your full category list here */ ];
  const subcategoryOptions = { /* your full subcategory object here */ };
  const ingredientOptions = [ /* your full ingredient list here */ ];

  const createEmptyIngredient = () => ({
    qty: "",
    unit: "",
    category: "",
    subcategory: "",
    ingredient: "",
    modifier: "",
    cost: "",
    isSubrecipe: false,
  });

  useEffect(() => {
    const saved = localStorage.getItem(recipeTitle);
    if (saved) {
      try {
        const data = JSON.parse(saved);
        setIngredients(Array.isArray(data.ingredients) ? data.ingredients : [createEmptyIngredient()]);
        setYieldAmount(data.yield || 10);
        setInstructions(data.instructions || "");
      } catch {
        setIngredients([createEmptyIngredient()]);
      }
    } else {
      setIngredients([createEmptyIngredient()]);
    }
  }, [recipeTitle]);

  const handleReturn = () => {
    if (returnTo) navigate(`/recipe?title=${returnTo}`);
  };

  const saveRecipe = () => {
    const recipeData = {
      title: recipeTitle,
      ingredients,
      yield: yieldAmount,
      instructions,
    };
    localStorage.setItem(recipeTitle, JSON.stringify(recipeData));
    alert("Recipe saved!");
  };

  const exportRecipe = () => {
    const recipeData = {
      title: recipeTitle,
      ingredients,
      yield: yieldAmount,
      instructions,
    };
    const blob = new Blob([JSON.stringify(recipeData, null, 2)], { type: "application/json" });
    const link = document.createElement("a");
    link.href = URL.createObjectURL(blob);
    link.download = `${recipeTitle}.json`;
    link.click();
  };

  const importRecipe = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    const reader = new FileReader();
    reader.onload = (evt) => {
      try {
        const data = JSON.parse(evt.target.result);
        if (data.title) setRecipeTitle(data.title);
        if (Array.isArray(data.ingredients)) setIngredients(data.ingredients);
        if (data.yield) setYieldAmount(data.yield);
        if (data.instructions) setInstructions(data.instructions);
      } catch {
        alert("Invalid recipe file");
      }
    };
    reader.readAsText(file);
  };

  return (
    <div className="min-h-screen bg-gray-100 py-6 px-4">
      <div className="max-w-6xl mx-auto bg-white shadow-md rounded-lg p-6 space-y-8">

        {/* Header */}
        <div className="flex justify-between items-center">
          <input
            value={recipeTitle}
            onChange={(e) => setRecipeTitle(e.target.value)}
            className="text-2xl font-bold text-green-900 w-full max-w-lg border-b-2 border-green-300 focus:outline-none"
          />
          {returnTo && (
            <button
              onClick={handleReturn}
              className="ml-4 text-sm text-blue-600 hover:underline"
            >
              ‚Üê Return to {returnTo}
            </button>
          )}
        </div>

        {/* Yield and Scaling */}
        <div>
          <h2 className="text-lg font-semibold text-gray-800 mb-2">Yield</h2>
          <div className="flex gap-4 items-center">
            <input
              type="number"
              value={yieldAmount}
              onChange={(e) => setYieldAmount(parseFloat(e.target.value))}
              className="border rounded px-3 py-2 w-28 text-sm"
              placeholder="e.g. 10"
            />
            <span className="text-sm text-gray-600">servings</span>
            <div className="flex gap-2 ml-4 text-sm">
              {[0.5, 1, 2, 4].map((factor) => (
                <button
                  key={factor}
                  onClick={() => setYieldAmount((prev) => Math.round(prev * factor))}
                  className="px-3 py-1 border rounded hover:bg-gray-100"
                >
                  {factor}x
                </button>
              ))}
            </div>
          </div>
        </div>

        {/* Ingredients */}
        <div>
          <h2 className="text-lg font-semibold text-gray-800 mb-2">Ingredients</h2>
          <IngredientManager
            ingredients={ingredients}
            setIngredients={setIngredients}
            createEmptyIngredient={createEmptyIngredient}
            recipeTitle={recipeTitle}
            unitOptions={unitOptions}
            categoryOptions={categoryOptions}
            subcategoryOptions={subcategoryOptions}
            ingredientOptions={ingredientOptions}
          />
        </div>

        {/* Instructions */}
        <div>
          <h2 className="text-lg font-semibold text-gray-800 mb-2">Instructions / Method</h2>
          <textarea
            value={instructions}
            onChange={(e) => setInstructions(e.target.value)}
            className="w-full border rounded px-3 py-2 text-sm"
            rows={6}
            placeholder="Describe how to prepare the dish, step by step."
          />
        </div>

        {/* Action Buttons */}
        <div className="flex gap-4">
          <button
            onClick={saveRecipe}
            className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700 text-sm"
          >
            Save Recipe
          </button>

          <button
            onClick={exportRecipe}
            className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 text-sm"
          >
            Export JSON
          </button>

          <label className="cursor-pointer bg-gray-200 px-4 py-2 rounded hover:bg-gray-300 text-sm">
            Import JSON
            <input
              type="file"
              accept="application/json"
              className="hidden"
              onChange={importRecipe}
            />
          </label>
        </div>

      </div>
    </div>
  );
}

export default RecipeEditor;
