import React, { useState, useEffect } from 'react';

const conversionMap = {
  g: { to: 'oz', factor: 0.035274 },
  oz: { to: 'g', factor: 28.3495 },
  ml: { to: 'cups', factor: 0.00422675 },
  cups: { to: 'ml', factor: 236.588 }
};

function convertUnit(qty, unit) {
  const entry = conversionMap[unit];
  if (!entry || !qty) return null;
  const convertedQty = (parseFloat(qty) * entry.factor).toFixed(2);
  return `${convertedQty} ${entry.to}`;
}

export default function RecipeEditor() {
  const [ingredientsCatalog, setIngredientsCatalog] = useState([]);
  const [lines, setLines] = useState(() => {
    const saved = localStorage.getItem('recipeLines');
    return saved ? JSON.parse(saved) : [{
      id: Date.now(),
      ingredientId: '',
      qty: '',
      unitType: '',
      epUnit: '',
      lineCost: 0
    }];
  });

  const [recipeMeta, setRecipeMeta] = useState(() => {
    const saved = JSON.parse(localStorage.getItem('recipeMeta'));
    return saved || { title: '', yield: '' };
  });

  useEffect(() => {
    const catalog = JSON.parse(localStorage.getItem('ingredientCatalog')) || [];
    setIngredientsCatalog(catalog);
  }, []);

  useEffect(() => {
    localStorage.setItem('recipeLines', JSON.stringify(lines));
    localStorage.setItem('recipeMeta', JSON.stringify(recipeMeta));
  }, [lines, recipeMeta]);

  function calculateLineCost(ingredientId, qty) {
    const ing = ingredientsCatalog.find(i => i.id === ingredientId);
    if (!ing || !qty) return 0;
    return parseFloat(((qty / ing.packSize) * ing.apCost).toFixed(2));
  }

  const handleChange = (id, field, value) => {
    setLines(prev =>
      prev.map(line => {
        if (line.id !== id) return line;
        const updated = { ...line, [field]: value };

        if (field === 'ingredientId') {
          const selected = ingredientsCatalog.find(i => i.id === value);
          if (selected) {
            updated.epUnit = selected.packUnit;
            updated.unitType = getUnitType(selected.packUnit);
          }
        }

        if (field === 'unitType') {
          updated.epUnit = '';
        }

        const qty = parseFloat(updated.qty) || 0;
        updated.lineCost = calculateLineCost(updated.ingredientId, qty);

        return updated;
      })
    );
  };

  function getUnitType(unit) {
    const weight = ['g', 'kg', 'oz', 'lb'];
    const volume = ['ml', 'l', 'tsp', 'Tbsp', 'cups', 'qt', 'gal'];
    const count = ['each', 'bunch', 'head', 'clove', 'slice', 'piece', 'egg'];
    if (weight.includes(unit)) return 'Weight';
    if (volume.includes(unit)) return 'Volume';
    if (count.includes(unit)) return 'Count';
    return '';
  }

  const addLine = () => {
    setLines(prev => [
      ...prev,
      {
        id: Date.now(),
        ingredientId: '',
        qty: '',
        unitType: '',
        epUnit: '',
        lineCost: 0
      }
    ]);
  };

  const removeLine = (id) => {
    setLines(prev => prev.filter(line => line.id !== id));
  };

  const totalCost = lines.reduce((sum, line) => sum + line.lineCost, 0);

  const groupedIngredients = ingredientsCatalog.reduce((acc, ing) => {
    const { category, subcategory } = ing;
    if (!acc[category]) acc[category] = {};
    if (!acc[category][subcategory]) acc[category][subcategory] = [];
    acc[category][subcategory].push(ing);
    return acc;
  }, {});

  return (
    <div className="p-6 max-w-5xl mx-auto bg-white shadow-lg rounded-lg font-satoshi">
      <h2 className="text-[28px] font-black text-[#050841] mb-6">Recipe Builder</h2>

      <div className="grid grid-cols-2 gap-4 mb-6">
        <div>
          <label className="block text-[14px] font-medium text-[#050841]">Title</label>
          <input
            type="text"
            value={recipeMeta.title}
            onChange={(e) => setRecipeMeta({ ...recipeMeta, title: e.target.value })}
            className="mt-1 block w-full border rounded p-2"
          />
        </div>
        <div>
          <label className="block text-[14px] font-medium text-[#050841]">Yield</label>
          <input
            type="text"
            value={recipeMeta.yield}
            onChange={(e) => setRecipeMeta({ ...recipeMeta, yield: e.target.value })}
            className="mt-1 block w-full border rounded p-2"
          />
        </div>
      </div>

      {lines.map((line) => {
        const converted = convertUnit(line.qty, line.epUnit);
        const selectedIngredient = ingredientsCatalog.find(i => i.id === line.ingredientId);
        const ingredientName = selectedIngredient?.name || '';
        const modifier = selectedIngredient?.modifier || '';

        return (
          <div
            key={line.id}
            className="grid grid-cols-12 gap-2 mb-4 items-center bg-gray-50 border border-gray-200 rounded p-4"
          >
            <div className="col-span-2">
              {line.qty && line.epUnit && (
                <p className="text-[16px] leading-[20px] text-[#050841] font-light">
                  <span className="underline text-[#050841]">{line.qty} {line.epUnit}</span>{' '}
                  {converted && (
                    <span className="italic text-gray-500 text-sm">({converted})</span>
                  )}
                </p>
              )}
            </div>

            <div className="col-span-5">
              <p className="text-[16px] text-[#050841] font-medium">
                {ingredientName}
                {modifier && (
                  <span className="ml-2 text-[15px] text-[#6B6E9B] font-light">{modifier}</span>
                )}
              </p>
            </div>

            <div className="col-span-1">
              <select
                value={line.unitType || ''}
                onChange={(e) => handleChange(line.id, 'unitType', e.target.value)}
                className="text-sm border rounded p-1 w-full"
              >
                <option value="">Type</option>
                <option value="Weight">Weight</option>
                <option value="Volume">Volume</option>
                <option value="Count">Count</option>
              </select>
            </div>

            <div className="col-span-1">
              <select
                value={line.epUnit}
                onChange={(e) => handleChange(line.id, 'epUnit', e.target.value)}
                className="text-sm border rounded p-1 w-full"
              >
                <option value="">Unit</option>
                {line.unitType === 'Weight' && (
                  <>
                    <option value="g">g</option>
                    <option value="kg">kg</option>
                    <option value="oz">oz</option>
                    <option value="lb">lb</option>
                  </>
                )}
                {line.unitType === 'Volume' && (
                  <>
                    <option value="ml">ml</option>
                    <option value="l">l</option>
                    <option value="tsp">tsp</option>
                    <option value="Tbsp">Tbsp</option>
                    <option value="cups">cups</option>
                    <option value="qt">qt</option>
                    <option value="gal">gal</option>
                  </>
                )}
                {line.unitType === 'Count' && (
                  <>
                    <option value="each">each</option>
                    <option value="bunch">bunch</option>
                    <option value="head">head</option>
                    <option value="clove">clove</option>
                    <option value="slice">slice</option>
                    <option value="piece">piece</option>
                    <option value="egg">egg</option>
                  </>
                )}
              </select>
            </div>

            <div className="col-span-2">
              <select
                value={line.ingredientId}
                onChange={(e) => handleChange(line.id, 'ingredientId', e.target.value)}
                className="text-sm border rounded p-1 w-full"
              >
                <option value="">Select ingredient...</option>
                {Object.entries(groupedIngredients).map(([cat, subs]) => (
                  <optgroup key={cat} label={cat}>
                    {Object.entries(subs).map(([subcat, items]) =>
                      items.map((ing) => (
                        <option key={ing.id} value={ing.id}>
                          {subcat} – {ing.name}
                        </option>
                      ))
                    )}
                  </optgroup>
                ))}
              </select>
            </div>

            <div className="text-right">
              <div className="text-lg font-bold text-gray-800">
                ${line.lineCost.toFixed(2)}
              </div>
              <button
                onClick={() => removeLine(line.id)}
                className="text-red-600 text-sm hover:underline mt-1"
              >
                Remove
              </button>
            </div>
          </div>
        );
      })}

      <div className="flex justify-between items-center mt-6">
        <button
          onClick={addLine}
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
        >
          + Add Ingredient
        </button>
        <div className="text-lg font-bold text-gray-800">
          Total Cost: ${totalCost.toFixed(2)}
        </div>
      </div>
    </div>
  );
}
