import React, { useState, useEffect } from 'react';

export default function IngredientManager() {
  const [ingredients, setIngredients] = useState(() => {
    const saved = localStorage.getItem('ingredientCatalog');
    return saved ? JSON.parse(saved) : [];
  });

  const [form, setForm] = useState({
    name: '',
    category: '',
    subcategory: '',
    apCost: '',
    packSize: '',
    packUnit: 'g'
  });

  useEffect(() => {
    localStorage.setItem('ingredientCatalog', JSON.stringify(ingredients));
  }, [ingredients]);

  const handleChange = (field, value) => {
    setForm(prev => ({ ...prev, [field]: value }));
  };

  const handleAdd = () => {
    if (!form.name || !form.category || !form.subcategory || !form.apCost || !form.packSize) return;

    const newIngredient = {
      id: Date.now().toString(),
      name: form.name,
      category: form.category,
      subcategory: form.subcategory,
      apCost: parseFloat(form.apCost),
      packSize: parseFloat(form.packSize),
      packUnit: form.packUnit
    };

    setIngredients(prev => [...prev, newIngredient]);
    setForm({ name: '', category: '', subcategory: '', apCost: '', packSize: '', packUnit: 'g' });
  };

  const groupByCategory = () => {
    const grouped = {};
    ingredients.forEach(ing => {
      if (!grouped[ing.category]) grouped[ing.category] = {};
      if (!grouped[ing.category][ing.subcategory]) grouped[ing.category][ing.subcategory] = [];
      grouped[ing.category][ing.subcategory].push(ing);
    });
    return grouped;
  };

  const groupedIngredients = groupByCategory();

  return (
    <div className="p-6 max-w-4xl mx-auto bg-white shadow-lg rounded-lg">
      <h2 className="text-2xl font-semibold mb-6 text-gray-800">Ingredient Catalog</h2>

      {/* Add New Ingredient */}
      <div className="grid grid-cols-3 gap-4 mb-6">
        <input
          type="text"
          placeholder="Name"
          value={form.name}
          onChange={(e) => handleChange('name', e.target.value)}
          className="border rounded p-2"
        />
        <input
          type="text"
          placeholder="Category"
          value={form.category}
          onChange={(e) => handleChange('category', e.target.value)}
          className="border rounded p-2"
        />
        <input
          type="text"
          placeholder="Subcategory"
          value={form.subcategory}
          onChange={(e) => handleChange('subcategory', e.target.value)}
          className="border rounded p-2"
        />
        <input
          type="number"
          placeholder="AP Cost ($)"
          value={form.apCost}
          onChange={(e) => handleChange('apCost', e.target.value)}
          className="border rounded p-2"
        />
        <input
          type="number"
          placeholder="Pack Size"
          value={form.packSize}
          onChange={(e) => handleChange('packSize', e.target.value)}
          className="border rounded p-2"
        />
        <select
          value={form.packUnit}
          onChange={(e) => handleChange('packUnit', e.target.value)}
          className="border rounded p-2"
        >
          <option value="g">grams</option>
          <option value="kg">kilograms</option>
          <option value="oz">ounces</option>
          <option value="lb">pounds</option>
          <option value="ml">milliliters</option>
          <option value="l">liters</option>
          <option value="each">each</option>
        </select>
      </div>
      <button
        onClick={handleAdd}
        className="mb-6 bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
      >
        + Add Ingredient
      </button>

      {/* Display Ingredients */}
      {Object.entries(groupedIngredients).map(([category, subs]) => (
        <div key={category} className="mb-4">
          <h3 className="text-xl font-bold text-blue-700">{category}</h3>
          {Object.entries(subs).map(([subcategory, items]) => (
            <div key={subcategory} className="ml-4">
              <h4 className="text-md font-semibold text-blue-500">{subcategory}</h4>
              <ul className="ml-4 list-disc">
                {items.map(ing => (
                  <li key={ing.id}>
                    {ing.name} – ${ing.apCost.toFixed(2)} per {ing.packSize} {ing.packUnit}
                  </li>
                ))}
              </ul>
            </div>
          ))}
        </div>
      ))}
    </div>
  );
}
