// IngredientManager.js (updated to work with RecipeEditor)
import React from 'react';
import IngredientRow from './IngredientRow';

export default function IngredientManager({ ingredientLines, onChange, onRemove, onAdd }) {
  return (
    <div>
      {ingredientLines.map((line, index) => (
        <IngredientRow
          key={index}
          index={index}
          line={line}
          onChange={onChange}
          onRemove={onRemove}
        />
      ))}
      <button
        className="mt-4 px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
        onClick={onAdd}
      >
        + Add Ingredient
      </button>
    </div>
  );
}
