import React, { useState } from "react";
import InstructionModal from "./InstructionModal";

function PrepInstructions({ instructions, setInstructions }) {
  const [editingIndex, setEditingIndex] = useState(null);

  const handleEdit = (index) => {
    setEditingIndex(index);
  };

  const closeModal = () => {
    setEditingIndex(null);
  };

  const updateInstruction = (index, updated) => {
    const updatedList = [...instructions];
    updatedList[index] = updated;
    setInstructions(updatedList);
  };

  const handleAddInstruction = () => {
    const newInstructions = [...instructions, { title: "", description: "" }];
    setInstructions(newInstructions);
    setEditingIndex(newInstructions.length - 1); // open modal after adding
  };

  return (
    <div className="space-y-2 max-w-[800px] w-full">
      <h2 className="text-lg font-semibold">Preparation Instructions</h2>

      {instructions.length === 0 && (
        <p className="text-sm italic text-gray-500">No instructions added yet.</p>
      )}

      {instructions.map((step, index) => (
        <button
          key={index}
          onClick={() => handleEdit(index)}
          className="text-left w-full text-sm px-1 py-1 hover:bg-gray-50 rounded transition"
        >
          <div className="flex items-start gap-2">
            <span className="font-semibold text-gray-800 w-5 text-right">
              {index + 1}.
            </span>
            <div className="space-y-0.5">
              <p className="font-semibold text-gray-900">
                {step.title || <em className="text-gray-400">No title</em>}
              </p>
              <p className="text-gray-800">
                {step.description || <em className="text-gray-400">No details provided.</em>}
              </p>
            </div>
          </div>
        </button>
      ))}

      <button
        onClick={handleAddInstruction}
        className="text-sm text-green-700 border border-green-600 px-3 py-.5 rounded hover:bg-green-50 mt-2"
      >
        âž• Add Instruction
      </button>

      {editingIndex !== null && (
        <InstructionModal
          step={instructions[editingIndex]}
          onSave={(updated) => {
            updateInstruction(editingIndex, updated);
            closeModal();
          }}
          onCancel={closeModal}
        />
      )}
    </div>
  );
}

export default PrepInstructions;
