// src/IngredientManager.js
import React from "react";
import IngredientRow from "./IngredientRow";

function IngredientManager({
  ingredients,
  setIngredients,
  createEmptyIngredient,
  recipeTitle,
}) {
  const updateIngredient = (index, updated) => {
    const updatedList = [...ingredients];
    updatedList[index] = updated;
    setIngredients(updatedList);
  };

  const removeIngredient = (index) => {
    const updatedList = [...ingredients];
    updatedList.splice(index, 1);
    setIngredients(updatedList);
  };

  const addIngredient = () => {
    setIngredients([...ingredients, createEmptyIngredient()]);
  };

  const totalCost = ingredients.reduce((sum, ing) => {
    const qty = parseFloat(ing.qty) || 0;
    const cost = parseFloat(ing.cost) || 0;
    return sum + qty * cost;
  }, 0).toFixed(2);

  return (
    <div className="space-y-2">
      <div className="font-semibold border-b pb-1 text-lg">Ingredients</div>

      <div className="overflow-x-auto">
        <div className="min-w-[1200px] space-y-1">
          {ingredients.map((ingredient, index) => (
            <IngredientRow
              key={index}
              ingredient={ingredient}
              index={index}
              updateIngredient={updateIngredient}
              removeIngredient={removeIngredient}
            />
          ))}
        </div>
      </div>

      <div className="flex justify-between items-center mt-4">
        <button
          onClick={addIngredient}
          className="bg-gray-800 text-white px-3 py-1 rounded hover:bg-gray-700"
        >
          + Add Ingredient
        </button>
        <div className="text-right font-semibold text-gray-700">
          Total Cost: <span className="font-mono">${totalCost}</span>
        </div>
      </div>
    </div>
  );
}

export default IngredientManager;
