// src/IngredientManager.js
import React, { useState } from "react";
import IngredientRow from "./IngredientRow";

function IngredientManager({ recipeTitle }) {
  const [ingredients, setIngredients] = useState([
    {
      qty: "",
      unit: "",
      category: "",
      subcategory: "",
      ingredient: "",
      modifier: "",
      cost: "",
      isSubrecipe: false,
    },
  ]);

  const updateIngredient = (index, updated) => {
    const copy = [...ingredients];
    copy[index] = updated;
    setIngredients(copy);
  };

  const removeIngredient = (index) => {
    const copy = [...ingredients];
    copy.splice(index, 1);
    setIngredients(copy);
  };

  const addIngredient = () => {
    setIngredients([
      ...ingredients,
      {
        qty: "",
        unit: "",
        category: "",
        subcategory: "",
        ingredient: "",
        modifier: "",
        cost: "",
        isSubrecipe: false,
      },
    ]);
  };

  return (
    <div>
      <div className="border border-gray-300 rounded p-4 mb-4 bg-white shadow-sm">
        <div className="font-semibold mb-2">Ingredients</div>
        {ingredients.map((ing, i) => (
          <IngredientRow
            key={i}
            ingredient={ing}
            index={i}
            updateIngredient={updateIngredient}
            removeIngredient={removeIngredient}
          />
        ))}
        <button
          onClick={addIngredient}
          className="mt-2 px-3 py-1 text-sm bg-blue-600 text-white rounded hover:bg-blue-700"
        >
          + Add Ingredient
        </button>
      </div>
    </div>
  );
}

export default IngredientManager;
