import React from 'react';
import ingredientsDB from './Ingredients';

function formatCurrency(amount) {
  return new Intl.NumberFormat('en-US', {
    style: 'currency',
    currency: 'USD',
    minimumFractionDigits: 2,
  }).format(amount);
}

function calculateCost(ingredientId, qty, unit) {
  const ingredient = ingredientsDB.find((ing) => ing.id === ingredientId);
  if (!ingredient || !qty || isNaN(qty)) return 0;

  const { apCost, packSize, packUnit, yield: yieldFactor, defaultEPUnit } = ingredient;
  const epQty = parseFloat(qty);

  let totalEPUnits = 1;
  if (packUnit === 'lb') {
    totalEPUnits = packSize * 16 * yieldFactor; // convert lb to oz
  } else if (packUnit === 'l') {
    totalEPUnits = packSize * 33.814 * yieldFactor; // convert liters to fl oz
  }

  const costPerEPUnit = apCost / totalEPUnits;
  return costPerEPUnit * epQty;
}

export default function IngredientRow({ index, line, onChange, onRemove }) {
  const ingredient = ingredientsDB.find((i) => i.id === line.ingredientId);
  const cost = calculateCost(line.ingredientId, line.qty, line.unit);

  return (
    <div className="flex flex-wrap items-end gap-x-2 gap-y-1 mb-2 pb-1 border-b border-gray-200">
      <input
        type="number"
        placeholder="Qty"
        className="text-[15px] w-[72px] border-none border-b border-gray-400 focus:outline-none underline font-medium text-gray-900 pl-[5px]"
        value={line.qty}
        onChange={(e) => onChange(index, 'qty', e.target.value)}
      />
      <select
        value={line.unit}
        onChange={(e) => onChange(index, 'unit', e.target.value)}
        className="text-[15px] w-[72px] border-none border-b border-gray-400 bg-transparent focus:outline-none underline font-medium text-gray-900 pl-[5px]"
      >
        <option value="oz">oz</option>
        <option value="g">g</option>
        <option value="fl oz">fl oz</option>
        <option value="tbsp">tbsp</option>
        <option value="tsp">tsp</option>
      </select>
      <select
        value={line.ingredientId}
        onChange={(e) => onChange(index, 'ingredientId', e.target.value)}
        className="text-[15px] w-[264px] border-none border-b border-gray-300 bg-transparent focus:outline-none font-semibold text-gray-900 pl-[5px]"
      >
        <option value="" disabled>
          Ingredient
        </option>
        {ingredientsDB.map((ing) => (
          <option key={ing.id} value={ing.id}>
            {ing.name}
          </option>
        ))}
      </select>
      <input
        type="text"
        placeholder="Modifier"
        className="text-[15px] italic text-gray-500 border-none border-b border-gray-200 bg-transparent focus:outline-none w-[144px] pl-[5px]"
        value={line.modifier}
        onChange={(e) => onChange(index, 'modifier', e.target.value)}
      />
      <span className="text-[15px] text-gray-600 min-w-[90px] pl-[5px]">
        {formatCurrency(cost)}
      </span>
      <button
        onClick={() => onRemove(index)}
        className="text-xs text-red-500 hover:text-red-700 pl-[5px]"
      >
        Remove
      </button>
    </div>
  );
}
