// HomePage.js
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { MoreVertical, Trash2, FileText, Copy, Upload, Printer } from "lucide-react";

function HomePage() {
  const navigate = useNavigate();
  const [recipes, setRecipes] = useState([]);
  const [menuOpenIndex, setMenuOpenIndex] = useState(null);

  useEffect(() => {
    const keys = Object.keys(localStorage);
    const validRecipes = [];

    keys.forEach((key) => {
      try {
        const val = JSON.parse(localStorage.getItem(key));
        if (val && typeof val.title === "string" && Array.isArray(val.ingredients)) {
          validRecipes.push({ key, title: val.title });
        }
      } catch (err) {
        console.warn(`⚠️ Could not parse ${key}`, err);
      }
    });

    setRecipes(validRecipes);
  }, []);

  const handleDelete = (key) => {
    if (window.confirm(`Delete this recipe?`)) {
      localStorage.removeItem(key);
      setRecipes(recipes.filter((r) => r.key !== key));
    }
  };

  const preloadDemos = () => {
    const demoRecipes = [
      {
        title: "Tomato Basil Soup",
        ingredients: [
          {
            qty: "500",
            unit: "g",
            category: "Produce",
            subcategory: "Tomatoes",
            ingredient: "Tomatoes",
            modifier: "",
            cost: "2.50",
            isSubrecipe: false,
          },
        ],
      },
      {
        title: "Buttermilk Pancakes",
        ingredients: [
          {
            qty: "1",
            unit: "cup",
            category: "Dairy & Eggs",
            subcategory: "Cultured Dairy",
            ingredient: "Buttermilk",
            modifier: "",
            cost: "0.80",
            isSubrecipe: false,
          },
        ],
      },
    ];

    demoRecipes.forEach((r) => {
      localStorage.setItem(r.title, JSON.stringify(r));
    });

    window.location.reload();
  };

  return (
    <div className="p-6 space-y-6 min-h-screen bg-gray-100 text-gray-900 font-satoshi">
      <div className="flex justify-between items-center">
        <div className="text-2xl font-bold">Recipes</div>
        <button
          onClick={preloadDemos}
          className="bg-blue-600 text-white px-3 py-1 rounded hover:bg-blue-700 text-sm"
        >
          Preload Demo Recipes
        </button>
      </div>

      <div className="space-y-2">
        {recipes.map((recipe, index) => (
          <div
            key={recipe.key}
            className="flex items-center justify-between bg-white px-4 py-3 rounded shadow hover:shadow-md transition"
          >
            <button
              className="text-left font-medium hover:underline"
              onClick={() => navigate(`/recipe?title=${encodeURIComponent(recipe.title)}`)}
            >
              {recipe.title}
            </button>

            <div className="relative">
              <button
                onClick={() =>
                  setMenuOpenIndex(index === menuOpenIndex ? null : index)
                }
                className="p-2 hover:bg-gray-200 rounded-full"
              >
                <MoreVertical size={20} />
              </button>

              {menuOpenIndex === index && (
                <div className="absolute right-0 mt-2 w-52 bg-white shadow-lg rounded-md border z-10">
                  <ul className="text-sm text-gray-700">
                    <li className="px-4 py-2 hover:bg-gray-100 cursor-pointer flex items-center gap-2">
                      <FileText size={16} /> Add to Recipe Book
                    </li>
                    <li className="px-4 py-2 hover:bg-gray-100 cursor-pointer flex items-center gap-2">
                      <Upload size={16} /> Send a copy
                    </li>
                    <li className="px-4 py-2 hover:bg-gray-100 cursor-pointer flex items-center gap-2">
                      <Copy size={16} /> Make a copy
                    </li>
                    <li className="px-4 py-2 hover:bg-gray-100 cursor-pointer flex items-center gap-2">
                      <Upload size={16} /> Export
                    </li>
                    <li className="px-4 py-2 hover:bg-gray-100 cursor-pointer flex items-center gap-2">
                      <Printer size={16} /> Print
                    </li>
                    <li
                      onClick={() => handleDelete(recipe.key)}
                      className="px-4 py-2 hover:bg-red-100 cursor-pointer text-red-600 flex items-center gap-2"
                    >
                      <Trash2 size={16} /> Delete
                    </li>
                  </ul>
                </div>
              )}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default HomePage;
