// RecipeEditor.js
import React, { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import IngredientManager from "./IngredientManager";
import PrepInstructions from "./PrepInstructions";
import { db } from "./Firebase";
import { doc, getDoc, setDoc } from "firebase/firestore";

function RecipeEditor() {
  const location = useLocation();
  const navigate = useNavigate();
  const searchParams = new URLSearchParams(location.search);
  const initialTitle = searchParams.get("title") || "Untitled Recipe";
  const returnTo = searchParams.get("returnTo");

  const sanitizeKey = (key) => key.replace(/[.#$\[\]/]/g, "_").trim() || "untitled";
  const [originalKey] = useState(sanitizeKey(initialTitle));

  const [recipeTitle, setRecipeTitle] = useState(initialTitle);
  const [source, setSource] = useState("");
  const [ingredients, setIngredients] = useState([]);
  const [instructions, setInstructions] = useState([]);
  const [portions, setPortions] = useState(1);
  const [menuPrice, setMenuPrice] = useState(0);

  const unitOptions = ["g", "kg", "oz", "lb", "cup", "tbsp", "tsp", "each"];
  const categoryOptions = ["Dairy & Eggs", "Meat", "Produce"];
  const subcategoryOptions = {
    "Dairy & Eggs": ["Milk", "Cheese", "Eggs"],
    Meat: ["Chicken", "Beef", "Pork"],
    Produce: ["Leafy Greens", "Root Vegetables", "Fruits"],
  };
  const ingredientOptions = [
    { name: "Milk", category: "Dairy & Eggs", subcategory: "Milk" },
    { name: "Cheddar Cheese", category: "Dairy & Eggs", subcategory: "Cheese" },
    { name: "Spinach", category: "Produce", subcategory: "Leafy Greens" },
    { name: "Carrots", category: "Produce", subcategory: "Root Vegetables" },
    { name: "Chicken Breast", category: "Meat", subcategory: "Chicken" },
  ];

  const createEmptyIngredient = () => ({
    qty: "",
    unit: "",
    category: "",
    subcategory: "",
    ingredient: "",
    modifier: "",
    cost: "",
    isSubrecipe: false,
  });

  useEffect(() => {
    const fetchRecipe = async () => {
      try {
        const docRef = doc(db, "recipes", originalKey);
        const docSnap = await getDoc(docRef);
        if (docSnap.exists()) {
          const data = docSnap.data();
          setIngredients(data.ingredients || []);
          setInstructions(data.instructions || []);
          setRecipeTitle(data.title || initialTitle);
          setSource(data.source || "");
          setPortions(data.portions || 1);
          setMenuPrice(data.menuPrice || 0);
        } else {
          setIngredients([createEmptyIngredient()]);
          setInstructions([]);
        }
      } catch (error) {
        console.error("Error loading recipe:", error);
      }
    };

    fetchRecipe();
  }, [originalKey]);

  const totalCost = ingredients.reduce((sum, ing) => {
    const qty = parseFloat(ing.qty) || 0;
    const cost = parseFloat(ing.cost) || 0;
    return sum + qty * cost;
  }, 0);

  const costPerPortion = portions > 0 ? totalCost / portions : 0;
  const foodCostPercent = menuPrice > 0 ? (costPerPortion / menuPrice) * 100 : 0;
  const profitPerPortion = menuPrice - costPerPortion;
  const suggestedMenuPrice = costPerPortion > 0 ? (costPerPortion / 0.35).toFixed(2) : "0.00";

  const saveRecipe = async () => {
    try {
      const docRef = doc(db, "recipes", sanitizeKey(recipeTitle));
      await setDoc(docRef, {
        title: recipeTitle,
        source,
        portions,
        menuPrice,
        ingredients,
        instructions,
        lastUpdated: new Date().toISOString(),
      });
      alert("Recipe saved!");
    } catch (err) {
      console.error("Error saving recipe:", err);
      alert("Failed to save recipe.");
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 text-gray-900 font-dm-sans p-6 space-y-6 max-w-screen-xl mx-auto">
      {/* Recipe Details */}
      <div className="bg-white border rounded p-4 shadow max-w-[800px] w-full">
        <h2 className="text-xl font-semibold mb-2">{recipeTitle}</h2>
        <p><strong>Source:</strong> <em>{source || "No source"}</em></p>
        <p><strong>Yield:</strong> {portions} portions</p>
        <p><strong>Menu Price:</strong> <em>${menuPrice.toFixed(2)}</em></p>
      </div>

      {/* Recipe Costing */}
      <div className="bg-white border rounded p-4 shadow max-w-[800px] w-full">
        <h2 className="text-lg font-semibold mb-1">Recipe Costing</h2>
        <div className="space-y-1 text-sm">
          <div className="flex justify-between">
            <span>Total Cost:</span>
            <span>${totalCost.toFixed(2)}</span>
          </div>
          <div className="flex justify-between">
            <span>Cost Per Portion:</span>
            <span>${costPerPortion.toFixed(2)}</span>
          </div>
          <div className="flex justify-between">
            <span>Suggested Min Price:</span>
            <span>${suggestedMenuPrice}</span>
          </div>
          <div className="flex justify-between">
            <span>Food Cost %:</span>
            <span>{foodCostPercent.toFixed(1)}%</span>
          </div>
          <div className="flex justify-between">
            <span>Profit $:</span>
            <span>${profitPerPortion.toFixed(2)}</span>
          </div>
        </div>
      </div>

      {/* Ingredients */}
      <div className="bg-white border rounded p-4 shadow max-w-[800px] w-full">
        <IngredientManager
          ingredients={ingredients}
          setIngredients={setIngredients}
          createEmptyIngredient={createEmptyIngredient}
          recipeTitle={recipeTitle}
          unitOptions={unitOptions}
          categoryOptions={categoryOptions}
          subcategoryOptions={subcategoryOptions}
          ingredientOptions={ingredientOptions}
        />
      </div>

      {/* Instructions */}
      <div className="bg-white border rounded p-4 shadow max-w-[800px] w-full">
        <PrepInstructions
          instructions={instructions}
          setInstructions={setInstructions}
        />
      </div>

      <button
        onClick={saveRecipe}
        className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
      >
        Save Recipe
      </button>
    </div>
  );
}

export default RecipeEditor;
