import React, { useState, useEffect } from "react";

function IngredientModal({ ingredient, onSave, onCancel }) {
  const [local, setLocal] = useState({ ...ingredient });

  useEffect(() => {
    setLocal({ ...ingredient });
  }, [ingredient]);

  const handleChange = (field, value) => {
    setLocal((prev) => ({ ...prev, [field]: value }));
  };

  const handleSubmit = () => {
    onSave(local);
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50">
      <div className="bg-white p-6 rounded-lg shadow-lg w-[600px] space-y-4">
        <h2 className="text-lg font-bold">Edit Ingredient</h2>

        <div className="grid grid-cols-2 gap-4">
          <div>
            <label className="block text-sm font-medium text-gray-700">Qty</label>
            <input
              type="text"
              value={local.qty || ""}
              onChange={(e) => handleChange("qty", e.target.value)}
              className="w-full border p-2 rounded"
            />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700">Unit</label>
            <input
              type="text"
              value={local.unit || ""}
              onChange={(e) => handleChange("unit", e.target.value)}
              className="w-full border p-2 rounded"
            />
          </div>
          <div className="col-span-2">
            <label className="block text-sm font-medium text-gray-700">Ingredient</label>
            <input
              type="text"
              value={local.ingredient || ""}
              onChange={(e) => handleChange("ingredient", e.target.value)}
              className="w-full border p-2 rounded"
            />
          </div>
          <div className="col-span-2">
            <label className="block text-sm font-medium text-gray-700">Modifier / Notes</label>
            <input
              type="text"
              value={local.modifier || ""}
              onChange={(e) => handleChange("modifier", e.target.value)}
              className="w-full border p-2 rounded"
            />
          </div>
        </div>

        <div className="flex justify-end gap-2 pt-4">
          <button
            onClick={onCancel}
            className="px-4 py-2 bg-gray-300 hover:bg-gray-400 rounded"
          >
            Cancel
          </button>
          <button
            onClick={handleSubmit}
            className="px-4 py-2 bg-green-600 text-white hover:bg-green-700 rounded"
          >
            Save
          </button>
        </div>
      </div>
    </div>
  );
}

export default IngredientModal;
