import React, { useState, useContext } from 'react';
import { RecipeContext } from './context/RecipeContext';

function NewRecipeForm({ onClose }) {
  const { addRecipe } = useContext(RecipeContext);
  const [rawIngredients, setRawIngredients] = useState('');
  const [rawMethod, setRawMethod] = useState('');

  const handleSave = () => {
    const lines = rawIngredients.split('\n');
    const ingredients = [];
    let currentSection = '';

    for (let line of lines) {
      line = line.trim();
      if (!line) continue;

      // Header section (e.g., "Dry Mix:")
      if (line.endsWith(':')) {
        currentSection = line.replace(':', '');
        continue;
      }

      // Match lines like "500g flour (sifted)"
      const match = line.match(/^([\d\/.\s\w]+)?\s*(.+?)(?:\s+\((.*?)\))?$/);
      if (match) {
        const quantity = match[1]?.trim() || '';
        const name = match[2]?.trim() || '';
        const note = match[3]?.trim() || '';

        ingredients.push({
          section: currentSection,
          name,
          quantity,
          note,
        });
      }
    }

    const recipe = {
      name: 'Untitled (Pasted)',
      yield: '',
      ingredients,
      method: rawMethod.trim(),
      rawIngredients,
    };

    addRecipe(recipe);
    onClose();
  };

  return (
    <div className="bg-white shadow-lg rounded p-6">
      <h2 className="text-2xl font-bold mb-4">Paste Ingredients & Method</h2>

      <label className="block text-sm font-semibold mb-1">Ingredients</label>
      <textarea
        className="w-full border p-2 mb-4 h-40"
        placeholder={`Example:\nDry Mix:\n500g flour\n1/2c semolina\nsalt to taste`}
        value={rawIngredients}
        onChange={(e) => setRawIngredients(e.target.value)}
      />

      <label className="block text-sm font-semibold mb-1">Method</label>
      <textarea
        className="w-full border p-2 mb-4 h-40"
        placeholder={`Example:\n1. Combine dry ingredients\n2. Mix wet ingredients\n3. Bake at 350°F`}
        value={rawMethod}
        onChange={(e) => setRawMethod(e.target.value)}
      />

      <div className="flex justify-end gap-4">
        <button
          onClick={onClose}
          className="bg-gray-300 px-4 py-2 rounded hover:bg-gray-400"
        >
          Cancel
        </button>
        <button
          onClick={handleSave}
          className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
        >
          Save Recipe
        </button>
      </div>
    </div>
  );
}

export default NewRecipeForm;
