// RecipeEditor.js
import React, { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import IngredientManager from "./IngredientManager";

function RecipeEditor() {
  const location = useLocation();
  const navigate = useNavigate();
  const searchParams = new URLSearchParams(location.search);
  const initialTitle = searchParams.get("title") || "Untitled Recipe";
  const returnTo = searchParams.get("returnTo");

  const [recipeTitle, setRecipeTitle] = useState(initialTitle);
  const [ingredients, setIngredients] = useState([]);
  const [portions, setPortions] = useState(1);
  const [menuPrice, setMenuPrice] = useState(0);

  const unitOptions = ["g", "kg", "oz", "lb", "cup", "tbsp", "tsp", "each"];
  const categoryOptions = ["Produce", "Herbs & Spices", "Dairy", "Meat"];
  const subcategoryOptions = {
    Produce: ["Nightshades", "Root Vegetables"],
    "Herbs & Spices": ["Fresh Herbs", "Dried Spices"],
    Dairy: ["Cheese", "Milk"],
    Meat: ["Red Meat", "Poultry"]
  };
  const ingredientOptions = [
    { name: "Tomatoes", category: "Produce", subcategory: "Nightshades" },
    { name: "Basil", category: "Herbs & Spices", subcategory: "Fresh Herbs" },
    { name: "Butter", category: "Dairy", subcategory: "Milk" },
    { name: "Chicken", category: "Meat", subcategory: "Poultry" }
  ];

  const createEmptyIngredient = () => ({
    qty: "",
    unit: "",
    category: "",
    subcategory: "",
    ingredient: "",
    modifier: "",
    cost: "",
    isSubrecipe: false
  });

  useEffect(() => {
    const saved = localStorage.getItem(recipeTitle);
    if (saved) {
      try {
        const data = JSON.parse(saved);
        if (data && Array.isArray(data.ingredients)) {
          setIngredients(data.ingredients);
        } else {
          setIngredients([createEmptyIngredient()]);
        }
      } catch {
        setIngredients([createEmptyIngredient()]);
      }
    } else {
      setIngredients([createEmptyIngredient()]);
    }
  }, [recipeTitle]);

  const totalCost = ingredients.reduce((sum, ing) => {
    const qty = parseFloat(ing.qty) || 0;
    const cost = parseFloat(ing.cost) || 0;
    return sum + qty * cost;
  }, 0);

  const costPerPortion = portions > 0 ? (totalCost / portions).toFixed(2) : "0.00";
  const foodCostPercent = menuPrice > 0 ? ((costPerPortion / menuPrice) * 100).toFixed(2) : "0.0";
  const profit = menuPrice - costPerPortion;

  const saveRecipe = () => {
    localStorage.setItem(recipeTitle, JSON.stringify({ title: recipeTitle, ingredients }));
    alert("Recipe saved!");
  };

  const exportRecipe = () => {
    const blob = new Blob([JSON.stringify({ title: recipeTitle, ingredients }, null, 2)], {
      type: "application/json"
    });
    const link = document.createElement("a");
    link.href = URL.createObjectURL(blob);
    link.download = `${recipeTitle}.json`;
    link.click();
  };

  const importRecipe = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    const reader = new FileReader();
    reader.onload = (evt) => {
      try {
        const data = JSON.parse(evt.target.result);
        if (data.title) setRecipeTitle(data.title);
        if (Array.isArray(data.ingredients)) {
          setIngredients(data.ingredients);
        } else {
          alert("Invalid file structure");
        }
      } catch {
        alert("Invalid file");
      }
    };
    reader.readAsText(file);
  };

  return (
    <div className="p-6 space-y-6 max-w-screen-xl mx-auto">
      {/* Header */}
      <div className="flex justify-between items-center mb-2">
        <input
          value={recipeTitle}
          onChange={(e) => setRecipeTitle(e.target.value)}
          className="text-2xl font-bold border-b px-2 py-1 w-full max-w-md"
        />
        {returnTo && (
          <button
            onClick={() => navigate(`/recipe?title=${returnTo}`)}
            className="text-sm text-blue-600 underline ml-4"
          >
            ‚Üê Return to {returnTo}
          </button>
        )}
      </div>

      {/* Ingredients Section */}
      <IngredientManager
        ingredients={ingredients}
        setIngredients={setIngredients}
        createEmptyIngredient={createEmptyIngredient}
        recipeTitle={recipeTitle}
        unitOptions={unitOptions}
        categoryOptions={categoryOptions}
        subcategoryOptions={subcategoryOptions}
        ingredientOptions={ingredientOptions}
      />

      {/* Recipe Costing Section */}
      <div className="bg-white p-4 rounded shadow">
        <div className="text-lg font-semibold border-b pb-2 mb-3">Recipe Costing</div>
        <div className="grid grid-cols-5 gap-6 items-end">
          <div>
            <label className="text-sm font-medium text-gray-700">Total Cost</label>
            <div className="text-base font-semibold text-gray-800">${totalCost.toFixed(2)}</div>
          </div>
          <div>
            <label className="text-sm font-medium text-gray-700">Portions</label>
            <input
              type="number"
              className="w-full px-2 py-1 border rounded text-sm"
              value={portions}
              onChange={(e) => setPortions(Number(e.target.value))}
            />
          </div>
          <div>
            <label className="text-sm font-medium text-gray-700">Cost Per Portion</label>
            <div className="text-base font-semibold text-gray-800">${costPerPortion}</div>
          </div>
          <div>
            <label className="text-sm font-medium text-gray-700">Menu Price</label>
            <input
              type="number"
              className="w-full px-2 py-1 border rounded text-sm"
              value={menuPrice}
              onChange={(e) => setMenuPrice(Number(e.target.value))}
            />
          </div>
          <div>
            <label className="text-sm font-medium text-gray-700">Food Cost %</label>
            <div className="text-base font-semibold text-gray-800">{foodCostPercent}%</div>
          </div>
        </div>
        <div className="mt-3 text-sm text-gray-700">
          <strong>Profit:</strong> ${profit.toFixed(2)}
        </div>
      </div>

      {/* Buttons */}
      <div className="flex gap-4">
        <button onClick={saveRecipe} className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700">
          Save Recipe
        </button>
        <button onClick={exportRecipe} className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700">
          Export JSON
        </button>
        <label className="bg-gray-200 px-4 py-2 rounded cursor-pointer hover:bg-gray-300">
          Import JSON
          <input type="file" className="hidden" accept="application/json" onChange={importRecipe} />
        </label>
      </div>
    </div>
  );
}

export default RecipeEditor;
