import React, { useContext, useState } from 'react';
import { RecipeContext } from './context/RecipeContext';

function App() {
  const {
    recipeName,
    setRecipeName,
    recipeYield,
    setRecipeYield,
    ingredients,
    setIngredients,
  } = useContext(RecipeContext);

  const [ingredientName, setIngredientName] = useState('');
  const [ingredientQty, setIngredientQty] = useState('');
  const [ingredientCost, setIngredientCost] = useState('');

  const addIngredient = () => {
    if (ingredientName && ingredientQty && ingredientCost) {
      const newIngredient = {
        name: ingredientName,
        quantity: ingredientQty,
        cost: ingredientCost,
      };
      setIngredients([...ingredients, newIngredient]);
      setIngredientName('');
      setIngredientQty('');
      setIngredientCost('');
    }
  };

  const saveRecipe = () => {
    const recipe = {
      name: recipeName,
      yield: recipeYield,
      ingredients: ingredients,
    };
    console.log('Saving Recipe:', recipe);
  };

  return (
    <div className="p-10 max-w-2xl mx-auto">
      <h1 className="text-3xl font-bold mb-6">Recipe MVP</h1>

      <input
        className="w-full p-2 border mb-2"
        placeholder="Recipe Name"
        value={recipeName}
        onChange={(e) => setRecipeName(e.target.value)}
      />
      <input
        className="w-full p-2 border mb-4"
        placeholder="Yield (portions)"
        value={recipeYield}
        onChange={(e) => setRecipeYield(e.target.value)}
      />

      <div className="mb-4">
        <h2 className="font-semibold mb-2">Add Ingredient</h2>
        <div className="flex gap-2 mb-2">
          <input
            className="flex-1 p-2 border"
            placeholder="Name"
            value={ingredientName}
            onChange={(e) => setIngredientName(e.target.value)}
          />
          <input
            className="w-20 p-2 border"
            placeholder="Qty"
            value={ingredientQty}
            onChange={(e) => setIngredientQty(e.target.value)}
          />
          <input
            className="w-24 p-2 border"
            placeholder="Cost"
            value={ingredientCost}
            onChange={(e) => setIngredientCost(e.target.value)}
          />
        </div>
        <button
          onClick={addIngredient}
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
        >
          Add Ingredient
        </button>
      </div>

      <button
        onClick={saveRecipe}
        className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
      >
        Save Recipe
      </button>

      <h3 className="mt-6 font-bold text-xl">Saved Ingredients</h3>
      <ul className="mt-2 space-y-1">
        {ingredients.map((item, index) => (
          <li key={index}>
            {item.name} – {item.quantity} – ${item.cost}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
