import React from "react";

function PrepInstructions({ instructions, setInstructions }) {
  const updateStep = (index, field, value) => {
    const updated = [...instructions];
    updated[index][field] = value;
    setInstructions(updated);
  };

  const addStep = () => {
    setInstructions((prev) => [
      ...prev,
      {
        stepNumber: prev.length + 1,
        title: "",
        description: "",
      },
    ]);
  };

  const removeStep = (index) => {
    const updated = instructions
      .filter((_, i) => i !== index)
      .map((step, i) => ({ ...step, stepNumber: i + 1 }));
    setInstructions(updated);
  };

  return (
    <div className="space-y-3 max-w-[800px] w-full">
      <h2 className="text-lg font-semibold">Preparation Instructions</h2>

      {instructions.map((step, index) => (
        <div
          key={index}
          className="grid grid-cols-[60px_1fr_auto] gap-x-2 gap-y-1 text-sm"
        >
          {/* Step number */}
          <div className="pt-1 font-medium text-gray-800">
            Step {index + 1}.
          </div>

          {/* Title input */}
          <input
            type="text"
            value={step.title}
            onChange={(e) => updateStep(index, "title", e.target.value)}
            placeholder="Step Title"
            className="w-full font-semibold border border-gray-300 rounded px-2 py-1 col-span-1"
          />

          {/* Remove button */}
          <button
            onClick={() => removeStep(index)}
            className="text-xs text-gray-400 hover:text-red-500"
          >
            ❌
          </button>

          {/* Spacer for alignment */}
          <div></div>

          {/* Detail textarea — aligned with title input */}
          <textarea
            value={step.description}
            onChange={(e) => updateStep(index, "description", e.target.value)}
            placeholder="Step details"
            rows={1}
            onInput={(e) => {
              e.target.style.height = "auto";
              e.target.style.height = `${e.target.scrollHeight}px`;
            }}
            className="w-full border border-gray-300 rounded px-2 py-1 resize-none overflow-hidden col-span-2"
          />
        </div>
      ))}

      <button
        onClick={addStep}
        className="bg-blue-600 text-white text-sm px-3 py-1 rounded hover:bg-blue-700"
      >
        ➕ Add Step
      </button>
    </div>
  );
}

export default PrepInstructions;
