import React from "react";
import { Menu } from "lucide-react";

const recipeCategories = [
  "Appetizer/Hors d'oeuvre",
  "Beverage",
  "Bread",
  "Breakfast",
  "Dessert/Pastry",
  "Garnish",
  "Intermezzo",
  "Mains",
  "Pasta",
  "Salad",
  "Sandwich",
  "Sauce",
  "Marinade/Seasoning",
  "Side",
  "Soup/Stew/Stock",
  "Mise en Place",
  "Classroom",
];

export default function RecipeCategoriesPanel({
  isOpen,
  setIsOpen,
  activeCategory,
  setActiveCategory,
  ownerFilter,
  setOwnerFilter,
  navigate,
}) {
  return (
    <>
      {/* Small Hamburger Button (now inline) */}
      <button
        onClick={() => setIsOpen(!isOpen)}
        className="absolute top-[90px] left-6 z-30 p-1 rounded-md hover:bg-[#F1F1FE] transition"
        title="Open Menu"
      >
        <Menu className="text-[#060841] w-5 h-5" />
      </button>

      {/* Slide-out Side Panel */}
      <div
        className={`fixed top-0 left-0 h-full w-64 bg-[#060841] text-white shadow-lg z-40 transition-transform duration-300 ease-in-out ${
          isOpen ? "translate-x-0" : "-translate-x-full"
        }`}
      >
        {/* Panel Header */}
        <div className="p-4 text-xl font-semibold border-b border-[#F1F1FE]">
          Menu
        </div>

        {/* Categories */}
        <ul className="mt-4 space-y-2 px-4">
          <div className="text-sm font-medium text-[#F1F1FE] mb-1">
            Categories
          </div>
          {recipeCategories.map((category) => (
            <li
              key={category}
              onClick={() => {
                setActiveCategory(category);
                setIsOpen(false);
              }}
              className={`text-sm cursor-pointer px-3 py-2 rounded-md transition ${
                activeCategory === category
                  ? "bg-[#F1F1FE] text-[#060841] font-semibold"
                  : "hover:bg-[#F1F1FE] hover:text-[#060841]"
              }`}
            >
              {category}
            </li>
          ))}
        </ul>

        {/* Divider */}
        <hr className="my-4 border-[#F1F1FE] mx-4" />

        {/* Owner Filter + Button */}
        <div className="px-4 space-y-2">
          <div className="text-sm font-medium text-[#F1F1FE]">
            Filter by Owner
          </div>
          {["All", "Personal", "First Concept"].map((option) => (
            <div
              key={option}
              onClick={() => {
                setOwnerFilter(option);
                setIsOpen(false);
              }}
              className={`text-sm px-3 py-1 rounded-md cursor-pointer ${
                ownerFilter === option
                  ? "bg-[#F1F1FE] text-[#060841] font-semibold"
                  : "hover:bg-[#F1F1FE] hover:text-[#060841]"
              }`}
            >
              {option}
            </div>
          ))}

          <button
            onClick={() => {
              navigate("/recipe");
              setIsOpen(false);
            }}
            className="mt-4 w-full bg-green-600 hover:bg-green-700 text-white text-sm py-2 rounded"
          >
            + New Recipe
          </button>
        </div>
      </div>
    </>
  );
}
