import React, { useState } from "react";
import { Menu } from "lucide-react";

const recipeCategories = [
  "Appetizer/Hors d'oeuvre",
  "Beverage",
  "Bread",
  "Breakfast",
  "Dessert/Pastry",
  "Garnish",
  "Intermezzo",
  "Mains",
  "Pasta",
  "Salad",
  "Sandwich",
  "Sauce",
  "Marinade/Seasoning",
  "Side",
  "Soup/Stew/Stock",
  "Mise en Place",
  "Classroom",
];

export default function RecipeCategoriesPanel() {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <>
      {/* Hamburger Menu Button */}
      <button
        onClick={() => setIsOpen(!isOpen)}
        className="fixed top-4 left-4 z-50 p-2 bg-[#F1F1FE] rounded-md shadow-md hover:bg-white"
      >
        <Menu className="text-[#060841] w-6 h-6" />
      </button>

      {/* Slide-Out Side Panel */}
      <div
        className={`fixed top-0 left-0 h-full w-64 bg-[#060841] text-white shadow-lg z-40 transition-transform duration-300 ease-in-out ${
          isOpen ? "translate-x-0" : "-translate-x-full"
        }`}
      >
        <div className="p-4 text-xl font-semibold border-b border-[#F1F1FE]">
          Recipe Categories
        </div>
        <ul className="mt-4 space-y-2 px-4">
          {recipeCategories.map((category) => (
            <li
              key={category}
              className="text-sm hover:bg-[#F1F1FE] hover:text-[#060841] px-3 py-2 rounded-md transition"
            >
              {category}
            </li>
          ))}
        </ul>
      </div>
    </>
  );
}
