import React, { useContext } from 'react';
import { RecipeContext } from './context/RecipeContext';
import NewRecipeForm from './NewRecipeForm';

function App() {
  const { recipes, clearRecipes } = useContext(RecipeContext);

  return (
    <div className="min-h-screen bg-gray-100 p-10 max-w-3xl mx-auto font-sans text-gray-800">
      <h1 className="text-3xl font-bold mb-6">Recipe MVP</h1>

      <NewRecipeForm />

      <div className="mt-10">
        <h3 className="font-bold text-xl mb-4">Saved Recipes</h3>

        <button
          onClick={clearRecipes}
          className="mb-6 bg-red-600 text-white px-4 py-2 rounded hover:bg-red-700"
        >
          Clear All Recipes
        </button>

        {recipes.length === 0 && (
          <p className="text-sm text-gray-500 italic">No recipes saved yet.</p>
        )}

        {recipes.map((r, index) => (
          <div key={index} className="mb-6 border p-4 rounded shadow-sm bg-white">
            <h4 className="text-lg font-semibold">
              {r.name || 'Untitled'} – {r.yield} portions
            </h4>

            <ul className="pl-5 list-disc text-sm text-gray-700 mt-2">
              {r.ingredients?.map((ing, i) => (
                <li key={i}>
                  {ing.name} – {ing.quantity} – ${ing.cost}
                </li>
              ))}
            </ul>

            {r.instructions?.length > 0 && (
              <ol className="pl-5 list-decimal mt-3 text-sm text-gray-700">
                {r.instructions.map((step, i) => (
                  <li key={i}>{step}</li>
                ))}
              </ol>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
