import React, { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import IngredientManager from "./IngredientManager";

function RecipeEditor() {
  const location = useLocation();
  const navigate = useNavigate();
  const searchParams = new URLSearchParams(location.search);
  const initialTitle = searchParams.get("title") || "Untitled Recipe";
  const returnTo = searchParams.get("returnTo");

  const [recipeTitle, setRecipeTitle] = useState(initialTitle);
  const [instructions, setInstructions] = useState("");
  const [ingredients, setIngredients] = useState([]);
  const [yieldQty, setYieldQty] = useState(10);
  const [scaleFactor, setScaleFactor] = useState(1);

  const unitOptions = ["g", "kg", "oz", "lb", "cup", "tbsp", "tsp", "each"];

  const categoryOptions = [
    "Baking Ingredients", "Dairy & Eggs", "Fermented & Pickled Items", "Flour & Milled Products",
    "Fruits", "Grains & Starches", "Herbs & Spices", "Nuts & Seeds", "Legumes & Beans",
    "Meat & Poultry", "Oils & Vinegars", "Pasta & Noodles", "Sauces & Condiments",
    "Seafood", "Stocks & Broths", "Sweeteners", "Produce", "Garnishes",
    "Prepared", "Bakery Items", "Frozen Items", "Miscellaneous", "Vegetables"
  ];

  const subcategoryOptions = {
    "Baking Ingredients": ["Chocolate", "Flavorings", "Leavening Agents", "Thickeners"],
    "Dairy & Eggs": ["Butter", "Cheese", "Cream", "Milk", "Cultured Dairy", "Whole (Shell) Eggs", "Liquid Eggs"],
    "Fermented & Pickled Items": ["Fermented"],
    "Flour & Milled Products": ["Flours", "Cornmeal", "Semolina"],
    "Fruits": ["Berries", "Citrus Fruits", "Dried Fruits", "Melons", "Stone Fruits", "Tropical Fruits"],
    "Grains & Starches": ["Barley", "Corn", "Couscous", "Farro", "Oats", "Quinoa", "Rice"],
    "Herbs & Spices": ["Dried Spices", "Fresh Herbs"],
    "Nuts & Seeds": ["Nuts", "Seeds"],
    "Legumes & Beans": ["Black Beans", "Chickpeas", "Kidney Beans", "Lentils", "Navy Beans", "Soybeans"],
    "Meat & Poultry": ["Beef", "Chicken", "Duck", "Game Meats", "Lamb", "Pork", "Processed Meats", "Turkey"],
    "Oils & Vinegars": ["Oils", "Vinegars"],
    "Pasta & Noodles": ["Egg Noodles", "Gluten-Free Pasta", "Rice Noodles", "Soba", "Udon"],
    "Sauces & Condiments": ["Common Sauces", "Condiments"],
    "Seafood": ["Finfish", "Mollusks", "Shellfish", "Cured/Smoked Seafood"],
    "Stocks & Broths": ["Stocks"],
    "Sweeteners": ["Natural Sweeteners", "Processed Sugars"],
    "Produce": ["Alliums", "Cruciferous Vegetables", "Leafy Greens", "Legumes", "Nightshades", "Root Vegetables"],
    "Garnishes": ["Finishing Touches", "Garnishes"],
    "Prepared": ["Garnishes & Toppings", "Prepped Proteins", "Prepped Starches", "Prepped Vegetables", "Sauces & Reductions", "Stocks & Broths", "Sub-Recipe"],
    "Bakery Items": ["Breads"],
    "Frozen Items": ["French Fries", "Frozen Fruit and Purees"],
    "Miscellaneous": ["Water"],
    "Vegetables": ["Canned / Preserved"]
  };

  const ingredientOptions = [
    "Milk", "Cheddar Cheese", "Ground Beef", "Chicken Breast",
    "Spinach", "Carrots", "Garlic", "Thyme", "Cinnamon", "Butter"
  ];

  const createEmptyIngredient = () => ({
    qty: "",
    unit: "",
    category: "",
    subcategory: "",
    ingredient: "",
    modifier: "",
    cost: "",
    isSubrecipe: false,
  });

  useEffect(() => {
    const saved = localStorage.getItem(recipeTitle);
    if (saved) {
      try {
        const data = JSON.parse(saved);
        if (data && Array.isArray(data.ingredients)) {
          setIngredients(data.ingredients);
          setInstructions(data.instructions || "");
          setYieldQty(data.yield || 10);
        } else {
          setIngredients([createEmptyIngredient()]);
        }
      } catch {
        setIngredients([createEmptyIngredient()]);
      }
    } else {
      setIngredients([createEmptyIngredient()]);
    }
  }, [recipeTitle]);

  const handleReturn = () => {
    if (returnTo) navigate(`/recipe?title=${returnTo}`);
  };

  const saveRecipe = () => {
    localStorage.setItem(
      recipeTitle,
      JSON.stringify({ title: recipeTitle, ingredients, instructions, yield: yieldQty })
    );
    alert("Recipe saved!");
  };

  const exportRecipe = () => {
    const blob = new Blob(
      [JSON.stringify({ title: recipeTitle, ingredients, instructions, yield: yieldQty }, null, 2)],
      { type: "application/json" }
    );
    const link = document.createElement("a");
    link.href = URL.createObjectURL(blob);
    link.download = `${recipeTitle}.json`;
    link.click();
  };

  const importRecipe = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    const reader = new FileReader();
    reader.onload = (evt) => {
      try {
        const data = JSON.parse(evt.target.result);
        if (data.title) setRecipeTitle(data.title);
        if (Array.isArray(data.ingredients)) setIngredients(data.ingredients);
        if (data.instructions) setInstructions(data.instructions);
        if (data.yield) setYieldQty(data.yield);
      } catch {
        alert("Invalid recipe file");
      }
    };
    reader.readAsText(file);
  };

  const totalCost = ingredients.reduce((sum, ing) => {
    const qty = parseFloat(ing.qty) || 0;
    const cost = parseFloat(ing.cost) || 0;
    return sum + qty * cost * scaleFactor;
  }, 0);

  return (
    <div className="min-h-screen bg-gray-100 font-sans">
      {/* Full Dashboard-Matched Header */}
      <header className="bg-[#14523F] text-white px-6 py-2 shadow">
        <div className="flex flex-col items-start sm:flex-row sm:items-center sm:justify-between">
          <div className="flex items-start gap-3">
            <img src="/ToqueWorks.png" alt="Toque Logo" className="h-28 w-auto object-contain" />
          </div>
        </div>
      </header>

      {/* Main Card */}
      <div className="max-w-6xl mx-auto mt-8 bg-white shadow rounded-lg p-6 space-y-6">
        {/* Title */}
        <div className="flex justify-between items-center border-b pb-2">
          <input
            value={recipeTitle}
            onChange={(e) => setRecipeTitle(e.target.value)}
            className="text-2xl font-bold border-b border-green-300 focus:outline-none w-full max-w-lg text-green-900"
          />
          {returnTo && (
            <button onClick={handleReturn} className="text-sm text-blue-600 hover:underline ml-4">
              ‚Üê Return to {returnTo}
            </button>
          )}
        </div>

        {/* Yield & Scale */}
        <div>
          <label className="block font-semibold mb-1">Yield</label>
          <div className="flex items-center gap-2">
            <input
              type="number"
              value={yieldQty}
              onChange={(e) => setYieldQty(parseFloat(e.target.value) || 1)}
              className="w-24 p-2 border rounded"
            />
            <span>servings</span>
            {[0.5, 1, 2, 4].map((factor) => (
              <button
                key={factor}
                onClick={() => setScaleFactor(factor)}
                className="px-3 py-1 border rounded hover:bg-gray-100"
              >
                {factor}x
              </button>
            ))}
          </div>
        </div>

        {/* Ingredients */}
        <div>
          <h3 className="text-lg font-semibold mb-2">Ingredients</h3>
          <IngredientManager
            ingredients={ingredients}
            setIngredients={setIngredients}
            createEmptyIngredient={createEmptyIngredient}
            recipeTitle={recipeTitle}
            unitOptions={unitOptions}
            categoryOptions={categoryOptions}
            subcategoryOptions={subcategoryOptions}
            ingredientOptions={ingredientOptions}
            scaleFactor={scaleFactor}
            showTitle={false}
          />
          <div className="text-right mt-2 font-semibold text-slate-800 text-lg">
            Total Cost: ${totalCost.toFixed(2)}
          </div>
        </div>

        {/* Instructions */}
        <div>
          <h3 className="text-lg font-semibold mb-2">Instructions / Method</h3>
          <textarea
            value={instructions}
            onChange={(e) => setInstructions(e.target.value)}
            rows={6}
            placeholder="Describe how to prepare the dish, step by step."
            className="w-full p-3 border rounded"
          />
        </div>

        {/* Buttons */}
        <div className="flex space-x-4">
          <button onClick={saveRecipe} className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700">
            Save Recipe
          </button>
          <button onClick={exportRecipe} className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700">
            Export JSON
          </button>
          <label className="cursor-pointer bg-gray-200 px-4 py-2 rounded hover:bg-gray-300">
            Import JSON
            <input type="file" accept="application/json" className="hidden" onChange={importRecipe} />
          </label>
        </div>
      </div>
    </div>
  );
}

export default RecipeEditor;
