import React, { useContext } from 'react';
import { RecipeContext } from './context/RecipeContext';
import NewRecipeForm from './NewRecipeForm';

function App() {
  const { recipes, clearRecipes } = useContext(RecipeContext);

  return (
    <div className="p-10 max-w-3xl mx-auto">
      <h1 className="text-3xl font-bold mb-6">Recipe MVP</h1>

      <NewRecipeForm />

      <div className="mt-10">
        <h3 className="font-bold text-xl mb-2">Saved Recipes</h3>
        <button
          onClick={clearRecipes}
          className="mb-4 bg-red-600 text-white px-4 py-2 rounded hover:bg-red-700"
        >
          Clear All Recipes
        </button>
        {recipes.map((r, index) => (
          <div key={index} className="mb-4 border p-4 rounded">
            <strong>{r.name || 'Untitled'}</strong> – {r.yield} portions
            <ul className="pl-4 list-disc text-sm text-gray-700 mt-2">
              {r.ingredients.map((ing, i) => (
                <li key={i}>
                  {ing.name} – {ing.quantity} – ${ing.cost}
                </li>
              ))}
            </ul>
            <ol className="pl-5 list-decimal mt-2 text-sm text-gray-700">
              {r.instructions?.map((step, i) => (
                <li key={i}>{step}</li>
              ))}
            </ol>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
