// src/RecipeEditor.js
import React from "react";
import { useLocation } from "react-router-dom";

function RecipeEditor() {
  const location = useLocation();
  const searchParams = new URLSearchParams(location.search);
  const title = searchParams.get("title") || "Untitled Recipe";
  const returnTo = searchParams.get("returnTo");

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-2">Editing Recipe: {title}</h1>
      {returnTo && (
        <p className="text-sm text-gray-600">
          You'll return to: <span className="font-mono">{returnTo}</span>
        </p>
      )}
      <p className="mt-4 text-gray-700">This is the recipe editor screen. Add your form here.</p>
    </div>
  );
}

export default RecipeEditor;
