// Dashboard.js
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import {
  MoreVertical, Trash2, FileText, Upload, Printer,
  Apple, BookOpen, Library, HelpCircle, Share2
} from "lucide-react";

function Dashboard() {
  const navigate = useNavigate();
  const [recipes, setRecipes] = useState([]);
  const [menuOpenIndex, setMenuOpenIndex] = useState(null);
  const [selected, setSelected] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [ownerFilter, setOwnerFilter] = useState("All");

  useEffect(() => {
    const keys = Object.keys(localStorage);
    const validRecipes = [];

    keys.forEach((key) => {
      try {
        const val = JSON.parse(localStorage.getItem(key));
        if (val && typeof val.title === "string" && Array.isArray(val.ingredients)) {
          validRecipes.push({
            key,
            title: val.title,
            owner: key.startsWith("personal") ? "Personal" : "First Concept",
            lastUpdated: val.lastUpdated || null
          });
        }
      } catch (err) {
        console.warn(`Could not parse ${key}`, err);
      }
    });

    setRecipes(validRecipes);
  }, []);

  const formatDate = (iso) => {
    if (!iso) return "—";
    return new Date(iso).toLocaleDateString(undefined, {
      year: "numeric", month: "short", day: "numeric"
    });
  };

  const toggleSelect = (key) => {
    setSelected((prev) =>
      prev.includes(key) ? prev.filter((id) => id !== key) : [...prev, key]
    );
  };

  const exportSelected = () => {
    selected.forEach((key) => {
      const raw = localStorage.getItem(key);
      if (!raw) return;
      const blob = new Blob([raw], { type: "application/json" });
      const a = document.createElement("a");
      a.href = URL.createObjectURL(blob);
      a.download = `${key}.json`;
      document.body.appendChild(a);
      a.click();
      a.remove();
    });
  };

  const sendSelected = async () => {
    for (const key of selected) {
      const raw = localStorage.getItem(key);
      if (!raw) continue;
      const file = new File([raw], `${key}.json`, { type: "application/json" });
      if (navigator.canShare?.({ files: [file] })) {
        try {
          await navigator.share({
            title: "Recipe: " + key,
            text: "Here's a recipe from the Recipe Dashboard",
            files: [file]
          });
        } catch (err) {
          console.warn("Share cancelled or not supported", err);
        }
      }
    }
  };

  const printSelected = () => {
    const html = selected.map((key) => {
      const raw = localStorage.getItem(key);
      if (!raw) return "";
      const recipe = JSON.parse(raw);
      const ingredients = recipe.ingredients.map(ing => `
        <tr>
          <td>${ing.qty}</td>
          <td>${ing.unit}</td>
          <td>${ing.ingredient}</td>
          <td>${ing.modifier || ""}</td>
          <td>${ing.category} / ${ing.subcategory}</td>
          <td>$${parseFloat(ing.cost).toFixed(2)}</td>
        </tr>
      `).join("");
      const total = recipe.ingredients.reduce((sum, i) => sum + parseFloat(i.cost || 0), 0);
      return `
        <section>
          <h1>${recipe.title}</h1>
          <p><strong>Printed:</strong> ${new Date().toLocaleString()}</p>
          <table>${ingredients}</table>
          <p><strong>Total:</strong> $${total.toFixed(2)}</p>
          ${recipe.instructions ? `<h2>Instructions</h2><p>${recipe.instructions}</p>` : ""}
        </section>
      `;
    }).join("<hr/>");

    const win = window.open("", "_blank");
    win.document.write(`<!DOCTYPE html><html><body>${html}</body></html>`);
    win.document.close();
    win.print();
  };

  const deleteSelected = () => {
    if (window.confirm(`Delete ${selected.length} recipe(s)?`)) {
      selected.forEach((key) => localStorage.removeItem(key));
      setRecipes((prev) => prev.filter((r) => !selected.includes(r.key)));
      setSelected([]);
    }
  };

  const filteredRecipes = recipes.filter((r) =>
    r.title.toLowerCase().includes(searchTerm.toLowerCase()) &&
    (ownerFilter === "All" || r.owner === ownerFilter)
  );

  return (
    <div className="min-h-screen bg-gray-100">
      <header className="bg-green-800 text-white px-6 py-4 flex justify-between items-center">
        <img src="/ToqueWorks.png" className="h-16" alt="Logo" />
        <nav className="flex gap-4">
          <button onClick={() => navigate("/")}><FileText /> Recipes</button>
          <button onClick={() => navigate("/ingredients")}><Apple /> Ingredients</button>
          <button onClick={() => navigate("/menus")}><BookOpen /> Menus</button>
          <button onClick={() => navigate("/books")}><Library /> Recipe Books</button>
        </nav>
      </header>

      <div className="p-4">
        <div className="flex justify-between items-center mb-4">
          <input
            type="text"
            placeholder="Search..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="border rounded px-2 py-1"
          />
          {selected.length > 0 && (
            <div className="flex gap-2">
              <button onClick={exportSelected}><FileText /></button>
              <button onClick={sendSelected}><Share2 /></button>
              <button onClick={printSelected}><Printer /></button>
              <button onClick={deleteSelected}><Trash2 /></button>
            </div>
          )}
        </div>

        {filteredRecipes.map((recipe, i) => (
          <div
            key={recipe.key}
            className={`flex justify-between items-center p-3 bg-white shadow mb-2 rounded ${selected.includes(recipe.key) ? 'bg-blue-50' : ''}`}
          >
            <div className="flex items-center gap-2">
              <input
                type="checkbox"
                checked={selected.includes(recipe.key)}
                onChange={() => toggleSelect(recipe.key)}
              />
              <button
                onClick={() => navigate(`/recipe?title=${encodeURIComponent(recipe.title)}`)}
                className="text-blue-600 hover:underline"
              >
                {recipe.title}
              </button>
            </div>
            <div className="text-gray-500 text-sm">
              {formatDate(recipe.lastUpdated)}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Dashboard;
