import React from 'react';
import ingredientsDB from './Ingredients';
import ingredientCategories from './IngredientCategories';

export default function IngredientRow({ index, line, onChange, onRemove }) {
  const handleChange = (field, value) => {
    onChange(index, field, value);
  };

  const subcategories =
    ingredientCategories.find((cat) => cat.category === line.category)?.subcategories || [];

  const filteredIngredients = ingredientsDB.filter(
    (i) => i.subcategory === line.subcategory
  );

  const ingredient = ingredientsDB.find((i) => i.id === line.ingredientId);
  const cost = ingredient ? `$${(ingredient.apCost || 0).toFixed(2)}` : '';

  return (
    <div className="flex gap-2 items-center mb-2">
      <input
        type="number"
        placeholder="Qty"
        className="w-[54px] h-[28px] text-sm font-normal text-gray-700 border-b border-gray-300 pl-[5px]"
        value={line.qty}
        onChange={(e) => handleChange('qty', e.target.value)}
      />

      <select
        className="w-[70px] h-[28px] text-sm font-normal border-b border-gray-300 text-gray-500 pl-[5px]"
        value={line.unit}
        onChange={(e) => handleChange('unit', e.target.value)}
      >
        <option value="g">g</option>
        <option value="oz">oz</option>
        <option value="ea">ea</option>
        <option value="qts">qts</option>
      </select>

      <select
        className="h-[28px] text-sm font-normal border-b border-gray-300 text-gray-500 pl-[5px] w-[180px]"
        value={line.category || ''}
        onChange={(e) => handleChange('category', e.target.value)}
      >
        <option value="">Select Category</option>
        {ingredientCategories.map((cat) => (
          <option key={cat.category} value={cat.category}>{cat.category}</option>
        ))}
      </select>

      <select
        className="h-[28px] text-sm font-normal border-b border-gray-300 text-gray-500 pl-[5px] w-[180px]"
        value={line.subcategory || ''}
        onChange={(e) => handleChange('subcategory', e.target.value)}
      >
        <option value="">Select Subcategory</option>
        {subcategories.map((sub) => (
          <option key={sub} value={sub}>{sub}</option>
        ))}
      </select>

      <select
        className="h-[28px] text-sm font-normal border-b border-gray-300 text-gray-500 pl-[5px] w-[180px]"
        value={line.ingredientId || ''}
        onChange={(e) => handleChange('ingredientId', e.target.value)}
      >
        <option value="">Select Ingredient</option>
        {filteredIngredients.map((ing) => (
          <option key={ing.id} value={ing.id}>{ing.name}</option>
        ))}
      </select>

      <input
        type="text"
        placeholder="Modifier"
        className="w-[150px] h-[28px] text-sm font-normal border-b border-gray-300 pl-[5px] text-gray-500"
        value={line.modifier || ''}
        onChange={(e) => handleChange('modifier', e.target.value)}
      />

      <span className="text-sm text-gray-700 w-[80px] h-[28px] flex items-center">{cost}</span>

      <button
        className="text-red-500 hover:text-red-700 text-sm ml-1 h-[28px]"
        onClick={() => onRemove(index)}
      >
        Remove
      </button>
    </div>
  );
}
