// Header.js
import React from "react";
import { useNavigate } from "react-router-dom";
import { FileText, Apple, BookOpen, Library } from "lucide-react";

function Header({ currentPage }) {
  const navigate = useNavigate();

  const navItems = [
    { label: "Recipes", icon: FileText, path: "/" },
    { label: "Ingredients", icon: Apple, path: "/ingredients" },
    { label: "Menus", icon: BookOpen, path: "/menus" },
    { label: "Recipe Books", icon: Library, path: "/books" },
  ];

  return (
    <header className="bg-[#14523F] text-white py-2 shadow">
      <div className="mx-auto w-full max-w-5xl flex flex-col sm:flex-row sm:items-center sm:justify-between px-6">
        <div className="flex items-center gap-6">
          <img
            src="/ToqueWorks.png"
            alt="Toque Logo"
            className="h-24 w-auto object-contain"
          />
        </div>
        <nav className="flex gap-6 mt-4 sm:mt-0">
          {navItems.map(({ label, icon: Icon, path }) => (
            <button
              key={label}
              onClick={() => navigate(path)}
              className={`text-base ${
                currentPage === label
                  ? "font-semibold border-b-2 border-white"
                  : "font-normal"
              } hover:text-gray-200 flex items-center gap-2`}
            >
              <Icon size={18} /> {label}
            </button>
          ))}
        </nav>
      </div>
    </header>
  );
}

export default Header;
