import React from "react";

function PrepInstructions({ instructions, setInstructions }) {
  const updateStep = (index, field, value) => {
    const updated = [...instructions];
    updated[index][field] = value;
    setInstructions(updated);
  };

  const addStep = () => {
    setInstructions((prev) => [
      ...prev,
      {
        stepNumber: prev.length + 1,
        title: "",
        description: "",
        isCCP: false,
        ccpNote: "",
      },
    ]);
  };

  const removeStep = (index) => {
    const updated = instructions
      .filter((_, i) => i !== index)
      .map((step, i) => ({ ...step, stepNumber: i + 1 }));
    setInstructions(updated);
  };

  return (
    <div className="space-y-4 max-w-[800px]">
      <h2 className="text-lg font-semibold">Preparation Instructions</h2>

      {instructions.map((step, index) => (
        <div key={index} className="text-sm space-y-1">
          {/* Title row */}
          <div className="flex items-center gap-2">
            <div className="flex items-center gap-1 w-full">
              <span className="text-gray-800 font-medium whitespace-nowrap">
                Step {index + 1}.
              </span>
              <input
                type="text"
                value={step.title}
                onChange={(e) => updateStep(index, "title", e.target.value)}
                placeholder="Step Title"
                className="flex-1 text-sm font-semibold border border-gray-300 rounded px-2 py-1"
              />
            </div>

            <label className="flex items-center text-xs text-red-600 ml-auto">
              <input
                type="checkbox"
                checked={step.isCCP || false}
                onChange={(e) =>
                  updateStep(index, "isCCP", e.target.checked)
                }
                className="mr-1"
              />
              CCP
            </label>

            <button
              onClick={() => removeStep(index)}
              className="text-xs text-gray-400 hover:text-red-500"
            >
              ❌
            </button>
          </div>

          {/* Detail row */}
          <div className="flex gap-2">
            <span className="w-[60px]" />
            <textarea
              value={step.description}
              onChange={(e) =>
                updateStep(index, "description", e.target.value)
              }
              placeholder="Step details"
              rows={1}
              onInput={(e) => {
                e.target.style.height = "auto";
                e.target.style.height = `${e.target.scrollHeight}px`;
              }}
              className="flex-1 text-sm border border-gray-300 rounded px-2 py-1 resize-none overflow-hidden"
            />
          </div>

          {/* Conditional CCP Note */}
          {step.isCCP && (
            <div className="flex gap-2 mt-1">
              <span className="w-[60px]" />
              <input
                type="text"
                value={step.ccpNote}
                onChange={(e) =>
                  updateStep(index, "ccpNote", e.target.value)
                }
                placeholder="CCP Note (e.g., Hold at ≥ 135°F)"
                className="flex-1 text-sm border border-yellow-400 rounded px-2 py-1 bg-yellow-50"
              />
            </div>
          )}
        </div>
      ))}

      <button
        onClick={addStep}
        className="bg-blue-600 text-white text-sm px-3 py-1 rounded hover:bg-blue-700"
      >
        ➕ Add Step
      </button>
    </div>
  );
}

export default PrepInstructions;
