// RecipeEditor.js
import React, { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import IngredientManager from "./IngredientManager";

function RecipeEditor() {
  const location = useLocation();
  const navigate = useNavigate();
  const searchParams = new URLSearchParams(location.search);
  const initialTitle = searchParams.get("title") || "Untitled Recipe";
  const returnTo = searchParams.get("returnTo");

  const [recipeTitle, setRecipeTitle] = useState(initialTitle);
  const [ingredients, setIngredients] = useState([]);
  const [portions, setPortions] = useState(1);
  const [menuPrice, setMenuPrice] = useState(0);

  const unitOptions = ["g", "kg", "oz", "lb", "cup", "tbsp", "tsp", "each"];

  const categoryOptions = ["Dairy & Eggs", "Meat", "Produce"];
  const subcategoryOptions = {
    "Dairy & Eggs": ["Milk", "Cheese", "Eggs"],
    "Meat": ["Chicken", "Beef", "Pork"],
    "Produce": ["Leafy Greens", "Root Vegetables", "Fruits"]
  };

  const ingredientOptions = [
    { name: "Milk", category: "Dairy & Eggs", subcategory: "Milk" },
    { name: "Cheddar Cheese", category: "Dairy & Eggs", subcategory: "Cheese" },
    { name: "Spinach", category: "Produce", subcategory: "Leafy Greens" },
    { name: "Carrots", category: "Produce", subcategory: "Root Vegetables" },
    { name: "Chicken Breast", category: "Meat", subcategory: "Chicken" }
  ];

  const createEmptyIngredient = () => ({
    qty: "",
    unit: "",
    category: "",
    subcategory: "",
    ingredient: "",
    modifier: "",
    cost: "",
    isSubrecipe: false
  });

  useEffect(() => {
    const saved = localStorage.getItem(recipeTitle);
    if (saved) {
      try {
        const data = JSON.parse(saved);
        if (data && Array.isArray(data.ingredients)) {
          setIngredients(data.ingredients);
        } else {
          setIngredients([createEmptyIngredient()]);
        }
      } catch {
        setIngredients([createEmptyIngredient()]);
      }
    } else {
      setIngredients([createEmptyIngredient()]);
    }
  }, [recipeTitle]);

  const totalCost = ingredients.reduce((sum, ing) => {
    const qty = parseFloat(ing.qty) || 0;
    const cost = parseFloat(ing.cost) || 0;
    return sum + qty * cost;
  }, 0);

  const costPerPortion = portions > 0 ? totalCost / portions : 0;
  const foodCostPercent = menuPrice > 0 ? (totalCost / menuPrice) * 100 : 0;

  const saveRecipe = () => {
    localStorage.setItem(recipeTitle, JSON.stringify({ title: recipeTitle, ingredients }));
    alert("Recipe saved!");
  };

  return (
    <div className="min-h-screen bg-gray-100 text-gray-900 font-dm-sans">
      <header className="bg-[#14523F] text-white px-6 py-2 shadow">
        <div className="flex flex-col items-start sm:flex-row sm:items-center sm:justify-between">
          <div className="flex items-start gap-3">
            <img src="/ToqueWorks.png" alt="Toque Logo" className="h-28 w-auto object-contain" />
          </div>
          <nav className="flex gap-6 mt-4 sm:mt-0">
            <button onClick={() => navigate("/")} className="text-base font-medium text-white border-b-2 border-white pb-1">Recipes</button>
            <button onClick={() => navigate("/ingredients")} className="text-base font-medium text-white hover:text-gray-200">Ingredients</button>
            <button onClick={() => navigate("/menus")} className="text-base font-medium text-white hover:text-gray-200">Menus</button>
            <button onClick={() => navigate("/books")} className="text-base font-medium text-white hover:text-gray-200">Recipe Books</button>
          </nav>
        </div>
      </header>

      <main className="p-6 space-y-6">
        <input
          value={recipeTitle}
          onChange={(e) => setRecipeTitle(e.target.value)}
          className="text-2xl font-bold w-1/2 pl-4 border-b-2 focus:outline-none"
        />

        <IngredientManager
          ingredients={ingredients}
          setIngredients={setIngredients}
          createEmptyIngredient={createEmptyIngredient}
          recipeTitle={recipeTitle}
          unitOptions={unitOptions}
          categoryOptions={categoryOptions}
          subcategoryOptions={subcategoryOptions}
          ingredientOptions={ingredientOptions}
        />

        {/* Recipe Costing Section */}
        <div className="bg-white p-4 shadow rounded border space-y-2 max-w-4xl">
          <h2 className="text-lg font-semibold border-b pb-1">Recipe Costing</h2>
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700">Total Cost</label>
              <div className="text-sm text-gray-900">${totalCost.toFixed(2)}</div>
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">Portions</label>
              <input
                type="number"
                value={portions}
                onChange={(e) => setPortions(parseInt(e.target.value) || 0)}
                className="mt-1 w-[80px] border px-2 py-1 rounded"
                placeholder="Servings"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">Cost Per Portion</label>
              <div className="text-sm text-gray-900">${costPerPortion.toFixed(2)}</div>
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">Menu Price</label>
              <input
                type="number"
                value={menuPrice}
                onChange={(e) => setMenuPrice(parseFloat(e.target.value) || 0)}
                className="mt-1 w-[100px] border px-2 py-1 rounded"
                placeholder="$ Menu Price"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">Food Cost %</label>
              <div className="text-sm text-gray-900">{foodCostPercent.toFixed(1)}%</div>
            </div>
          </div>
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">Instructions</label>
          <textarea
            className="w-full border px-2 py-2 rounded min-h-[120px]"
            placeholder="Add preparation steps here (coming soon...)"
          ></textarea>
        </div>

        <button
          onClick={saveRecipe}
          className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
        >
          Save Recipe
        </button>
      </main>
    </div>
  );
}

export default RecipeEditor;
